const P = parseFloat(inputs.loanAmount) || 0;
const annualRate = parseFloat(inputs.interestRate) || 0;
const termYears = parseFloat(inputs.loanTerm) || 0;
const extra = parseFloat(inputs.extraPayment) || 0;

if (P <= 0 || annualRate <= 0 || termYears <= 0) {
  return {
    monthlyPayment: 0,
    totalCost: P,
    totalInterest: 0,
    payoffYears: termYears,
    interestSaved: 0
  };
}

const r = (annualRate / 100) / 12;
const n = termYears * 12;

const standardMonthlyPayment = P * (r * Math.pow(1 + r, n)) / (Math.pow(1 + r, n) - 1);

if (isNaN(standardMonthlyPayment)) {
   return {
    monthlyPayment: 0,
    totalCost: P,
    totalInterest: 0,
    payoffYears: termYears,
    interestSaved: 0
  };
}

const originalTotalCost = standardMonthlyPayment * n;
const originalTotalInterest = originalTotalCost - P;

if (extra <= 0) {
  return {
    monthlyPayment: standardMonthlyPayment,
    totalCost: originalTotalCost,
    totalInterest: originalTotalInterest,
    payoffYears: termYears,
    interestSaved: 0
  };
}

const totalMonthlyPayment = standardMonthlyPayment + extra;

if (totalMonthlyPayment <= P * r) {
  return {
    monthlyPayment: totalMonthlyPayment,
    totalCost: Infinity,
    totalInterest: Infinity,
    payoffYears: Infinity,
    interestSaved: 0
  };
}

const newN = -Math.log(1 - (P * r) / totalMonthlyPayment) / Math.log(1 + r);
const newPayoffYears = newN / 12;

const newTotalCost = totalMonthlyPayment * newN;
const newTotalInterest = newTotalCost - P;

const interestSaved = originalTotalInterest - newTotalInterest;

return {
  monthlyPayment: totalMonthlyPayment,
  totalCost: newTotalCost,
  totalInterest: newTotalInterest,
  payoffYears: newPayoffYears,
  interestSaved: interestSaved > 0 ? interestSaved : 0
};
