function calculateOutputs(inputs) {
  const { perceivedSafetyIndex, reportedBullyingIncidentsPer1000, mentalHealthSupportAccessScore, safetyProtocolImplementationScore, staffTrainingFrequencyAnnually, schoolClimateSurveyScore, parentCommunityEngagementScore } = inputs;

  // --- Input Normalization (0-100 scale, higher is better for positive factors) ---
  // Perceived Safety Index: 1-10 -> 0-100
  const normPerceivedSafety = ((perceivedSafetyIndex - 1) / 9) * 100;

  // Reported Bullying Incidents: lower is better. Max assumed 50 incidents/1000 for scaling.
  const clampedBullying = Math.min(Math.max(reportedBullyingIncidentsPer1000, 0), 50);
  const normBullyingInverse = (1 - (clampedBullying / 50)) * 100;

  // Mental Health Support Access: 1-5 -> 0-100
  const normMHScope = ((mentalHealthSupportAccessScore - 1) / 4) * 100;

  // Safety Protocol Implementation: 1-5 -> 0-100
  const normSafetyProtocols = ((safetyProtocolImplementationScore - 1) / 4) * 100;

  // Staff Training Frequency: Max 4 effective trainings/year for scaling. Higher is better.
  const clampedTraining = Math.min(Math.max(staffTrainingFrequencyAnnually, 0), 4);
  const normTraining = (clampedTraining / 4) * 100;

  // School Climate Survey Score: 1-10 -> 0-100
  const normSchoolClimate = ((schoolClimateSurveyScore - 1) / 9) * 100;

  // Parent/Community Engagement: 1-5 -> 0-100
  const normParentEngagement = ((parentCommunityEngagementScore - 1) / 4) * 100;

  // --- Output Calculation ---

  // 1. Academic Performance Impact Percentage
  // Key drivers: Perceived Safety, Bullying, School Climate.
  // Positive factors for academic performance:
  const academicFactor = 
    (normPerceivedSafety * 0.40) + 
    (normBullyingInverse * 0.35) + 
    (normSchoolClimate * 0.25);

  // Map academicFactor (0-100) to academicPerformanceImpactPercentage (-10% to +5%)
  // 0 -> -10%, 50 -> 0%, 100 -> +5%
  let academicPerformanceImpactPercentage = ((academicFactor / 100) * 15) - 10;
  academicPerformanceImpactPercentage = Math.round(academicPerformanceImpactPercentage * 100) / 100; // Round to 2 decimal places

  // 2. Student Mental Health Impact Score (0-100, higher is worse impact)
  // Key drivers: (lack of) Perceived Safety, Bullying, (lack of) MH Support, (poor) School Climate.
  const mentalHealthImpactScore = 
    ((100 - normPerceivedSafety) * 0.30) + 
    ((100 - normBullyingInverse) * 0.35) + 
    ((100 - normMHScope) * 0.25) + 
    ((100 - normSchoolClimate) * 0.10);

  // Cap mentalHealthImpactScore between 0 and 100
  const finalMentalHealthImpactScore = Math.max(0, Math.min(100, mentalHealthImpactScore));

  // 3. Overall Learning Environment Risk Score (0-100, higher is higher risk)
  // Holistic view: Incorporates all factors.
  const overallLearningEnvironmentRiskScore = 
    ((100 - normPerceivedSafety) * 0.25) + 
    ((100 - normBullyingInverse) * 0.20) + 
    ((100 - normMHScope) * 0.15) + 
    ((100 - normSafetyProtocols) * 0.15) + 
    ((100 - normTraining) * 0.10) + 
    ((100 - normSchoolClimate) * 0.10) + 
    ((100 - normParentEngagement) * 0.05);

  // Cap overallLearningEnvironmentRiskScore between 0 and 100
  const finalOverallLearningEnvironmentRiskScore = Math.max(0, Math.min(100, overallLearningEnvironmentRiskScore));

  return {
    academicPerformanceImpactPercentage: academicPerformanceImpactPercentage,
    mentalHealthImpactScore: finalMentalHealthImpactScore,
    overallLearningEnvironmentRiskScore: finalOverallLearningEnvironmentRiskScore
  };
}
