formula:
{
    // 1. Sanitize and validate inputs
    const totalBudget = Math.max(0, inputs.totalBudget);
    const totalStudents = Math.max(0, inputs.totalStudents);
    const highNeedsPercentage = Math.min(100, Math.max(0, inputs.highNeedsPercentage));
    let academicEffectiveness = Math.min(1, Math.max(0, inputs.academicEffectiveness));
    let mentalHealthEffectiveness = Math.min(1, Math.max(0, inputs.mentalHealthEffectiveness));
    let basicNeedsEffectiveness = Math.min(1, Math.max(0, inputs.basicNeedsEffectiveness));
    const highNeedsImpactFactor = Math.max(1, inputs.highNeedsImpactFactor);
    const baseAllocationPerStudent = Math.max(0, inputs.baseAllocationPerStudent);

    // 2. Early exit for zero conditions
    if (totalBudget <= 0 || totalStudents <= 0) {
        return {
            allocatedAcademicSupport: 0,
            allocatedMentalHealth: 0,
            allocatedBasicNeeds: 0,
            totalAllocated: 0,
            unallocatedFunds: totalBudget
        };
    }

    // 3. Calculate student demographics
    const numHighNeedsStudents = totalStudents * (highNeedsPercentage / 100);

    // 4. Calculate 'core' funding requirements based on base allocation and high-needs premium
    const coreBaseAllocationPool = totalStudents * baseAllocationPerStudent; // Funding for all students at base level
    const highNeedsPremiumPool = numHighNeedsStudents * baseAllocationPerStudent * (highNeedsImpactFactor - 1); // Additional funding for high-needs students
    const totalCoreNeedsDemand = coreBaseAllocationPool + highNeedsPremiumPool; // Total amount needed to cover base + high-needs premium

    // 5. Normalize effectiveness weights for distribution
    let totalEffectivenessWeight = academicEffectiveness + mentalHealthEffectiveness + basicNeedsEffectiveness;
    if (totalEffectivenessWeight === 0) {
        // If all effectiveness scores are zero, distribute funds evenly across categories as a default.
        totalEffectivenessWeight = 3; 
        academicEffectiveness = 1; // Temporarily set to 1 for even distribution
        mentalHealthEffectiveness = 1;
        basicNeedsEffectiveness = 1;
    }
    const normAcademic = academicEffectiveness / totalEffectivenessWeight;
    const normMentalHealth = mentalHealthEffectiveness / totalEffectivenessWeight;
    const normBasicNeeds = basicNeedsEffectiveness / totalEffectivenessWeight;

    let allocatedAcademicSupport = 0;
    let allocatedMentalHealth = 0;
    let allocatedBasicNeeds = 0;
    let totalAllocated = 0;
    let unallocatedFunds = 0;

    // 6. Distribute funds based on budget availability relative to core needs
    if (totalCoreNeedsDemand <= totalBudget) {
        // Case A: Budget is sufficient for or exceeds core needs.
        // First, distribute the 'totalCoreNeedsDemand' across programs based on normalized weights.
        allocatedAcademicSupport = totalCoreNeedsDemand * normAcademic;
        allocatedMentalHealth = totalCoreNeedsDemand * normMentalHealth;
        allocatedBasicNeeds = totalCoreNeedsDemand * normBasicNeeds;

        // Distribute any remaining budget (strategic allocation) based on the same normalized weights.
        const strategicBudgetRemaining = totalBudget - totalCoreNeedsDemand;
        allocatedAcademicSupport += strategicBudgetRemaining * normAcademic;
        allocatedMentalHealth += strategicBudgetRemaining * normMentalHealth;
        allocatedBasicNeeds += strategicBudgetRemaining * normBasicNeeds;

        totalAllocated = allocatedAcademicSupport + allocatedMentalHealth + allocatedBasicNeeds;
        unallocatedFunds = totalBudget - totalAllocated;

    } else {
        // Case B: Budget is insufficient for core needs. Scale down the core needs proportionally to fit the budget.
        const scaleFactor = totalBudget / totalCoreNeedsDemand;

        allocatedAcademicSupport = (totalCoreNeedsDemand * normAcademic) * scaleFactor;
        allocatedMentalHealth = (totalCoreNeedsDemand * normMentalHealth) * scaleFactor;
        allocatedBasicNeeds = (totalCoreNeedsDemand * normBasicNeeds) * scaleFactor;

        totalAllocated = allocatedAcademicSupport + allocatedMentalHealth + allocatedBasicNeeds;
        unallocatedFunds = totalBudget - totalAllocated; // Should be very close to 0
    }

    // 7. Round results to two decimal places for currency
    return {
        allocatedAcademicSupport: Math.round(allocatedAcademicSupport * 100) / 100,
        allocatedMentalHealth: Math.round(allocatedMentalHealth * 100) / 100,
        allocatedBasicNeeds: Math.round(allocatedBasicNeeds * 100) / 100,
        totalAllocated: Math.round(totalAllocated * 100) / 100,
        unallocatedFunds: Math.round(unallocatedFunds * 100) / 100
    };
}
