function calculate({ annualRevenue, grossProfitMargin, criticalSCDependency, avgDisruptionFrequency, avgDisruptionDuration, dailyOperationalCost, resilienceInvestment, resilienceImpactReduction, recoveryTimeReduction }) {

  // Input validation and normalization
  annualRevenue = Math.max(0, annualRevenue || 0);
  grossProfitMargin = Math.min(100, Math.max(0, grossProfitMargin || 0));
  criticalSCDependency = Math.min(100, Math.max(0, criticalSCDependency || 0));
  avgDisruptionFrequency = Math.max(0, avgDisruptionFrequency || 0);
  avgDisruptionDuration = Math.max(0, avgDisruptionDuration || 0);
  dailyOperationalCost = Math.max(0, dailyOperationalCost || 0);
  resilienceInvestment = Math.max(0, resilienceInvestment || 0);
  resilienceImpactReduction = Math.min(100, Math.max(0, resilienceImpactReduction || 0));
  recoveryTimeReduction = Math.min(100, Math.max(0, recoveryTimeReduction || 0));

  const daysInYear = 365;

  // 1. Calculate the daily gross profit at risk
  const dailyGrossProfit = (annualRevenue * (grossProfitMargin / 100)) / daysInYear;
  const dailyProfitAtRisk = dailyGrossProfit * (criticalSCDependency / 100);

  // 2. Calculate the financial loss for a single disruption without resilience
  const financialLossPerDisruptionWithoutResilience = (dailyProfitAtRisk + dailyOperationalCost) * avgDisruptionDuration;

  // 3. Calculate the total annual loss without resilience
  const potentialLossWithoutResilience = financialLossPerDisruptionWithoutResilience * avgDisruptionFrequency;

  // 4. Calculate the impact with resilience strategies
  const actualImpactReductionFactor = 1 - (resilienceImpactReduction / 100);
  const actualRecoveryTimeReductionFactor = 1 - (recoveryTimeReduction / 100);

  const mitigatedDisruptionDuration = avgDisruptionDuration * actualRecoveryTimeReductionFactor;

  // Ensure duration doesn't go below zero for calculation sanity, though input range should prevent it.
  const effectiveMitigatedDisruptionDuration = Math.max(0, mitigatedDisruptionDuration);

  // 5. Calculate the financial loss for a single disruption *with* resilience
  const financialLossPerDisruptionWithResilience = (dailyProfitAtRisk + dailyOperationalCost) * effectiveMitigatedDisruptionDuration * actualImpactReductionFactor;

  // 6. Calculate the total annual loss with resilience
  const residualLossWithResilience = financialLossPerDisruptionWithResilience * avgDisruptionFrequency;

  // 7. Calculate the net financial benefit of resilience investment
  const avoidedLosses = potentialLossWithoutResilience - residualLossWithResilience;
  const netBenefitOfResilience = avoidedLosses - resilienceInvestment;

  // 8. Calculate the Return on Investment (ROI)
  let roiOfResilience = 0;
  if (resilienceInvestment > 0) {
    roiOfResilience = (netBenefitOfResilience / resilienceInvestment) * 100;
  } else if (netBenefitOfResilience > 0) {
    // If no investment but there's a benefit, indicate high (or effectively infinite) ROI from implied prior actions/luck.
    // For practical purposes, if investment is 0, and there's a benefit, this is more conceptual.
    // A common approach is to return a very high number or handle as a special case. Let's return 0 for practical interpretation if no investment is explicitly made.
    roiOfResilience = Infinity; // Or 0 if an actual investment is required for ROI
  } else {
      roiOfResilience = 0; // No investment, no benefit.
  }

  // 9. Calculate avoided annual downtime
  const totalAnnualDowntimeWithoutResilience = avgDisruptionDuration * avgDisruptionFrequency;
  const totalAnnualDowntimeWithResilience = effectiveMitigatedDisruptionDuration * avgDisruptionFrequency;
  const avoidedDowntime = totalAnnualDowntimeWithoutResilience - totalAnnualDowntimeWithResilience;

  return {
    potentialLossWithoutResilience,
    residualLossWithResilience,
    netBenefitOfResilience,
    roiOfResilience: isFinite(roiOfResilience) ? roiOfResilience : (netBenefitOfResilience > 0 ? 999999999 : 0), // Handle Infinity for display purposes
    avoidedDowntime: Math.max(0, avoidedDowntime)
  };
}
