function calculateTariffImpact(inputs) {
    const unitCostOrigin = inputs.unit_cost_origin || 0;
    const freightInsuranceBrokeragePerUnit = inputs.freight_insurance_brokerage_per_unit || 0;
    const existingImportDutyRate = Math.max(0, (inputs.existing_import_duty_rate || 0)) / 100; // Ensure non-negative, convert to decimal
    const newTariffRate = Math.max(0, (inputs.new_tariff_rate || 0)) / 100; // Ensure non-negative, convert to decimal
    const isReExportScenario = inputs.is_re_export_scenario || false; // Default to false
    const reExportMarkupPercentage = Math.max(0, (inputs.re_export_markup_percentage || 0)) / 100; // Ensure non-negative, convert to decimal
    const reExportDutyRate = Math.max(0, (inputs.re_export_duty_rate || 0)) / 100; // Ensure non-negative, convert to decimal

    // --- Step 1: Calculate the core cost basis for initial logistics and existing duties ---
    // This typically includes the unit cost and other transport/handling costs up to the initial import point.
    const initialCostBasis = unitCostOrigin + freightInsuranceBrokeragePerUnit;

    // Calculate existing import duties. These apply to the initial dutiable value.
    const existingDutiesCost = initialCostBasis * existingImportDutyRate;

    // --- Step 2: Calculate costs related to re-export scenario if applicable ---
    let reExportDutiesCost = 0;

    if (isReExportScenario) {
        // The value of goods at re-export should reflect all accumulated costs plus any markup.
        // Markup is applied to the unitCostOrigin for value-add, then re-export duty on that new value.
        const valueAtReExportBeforeDuty = unitCostOrigin * (1 + reExportMarkupPercentage);
        reExportDutiesCost = valueAtReExportBeforeDuty * reExportDutyRate;
    }

    // --- Step 3: Determine Total Landed Cost (Pre-New Tariff) ---
    // This includes original cost, freight, insurance, brokerage, existing duties, and re-export duties (if any).
    const totalLandedCostPreNewTariff = initialCostBasis + existingDutiesCost + reExportDutiesCost;

    // --- Step 4: Calculate the cost basis for the NEW tariff application and its impact ---
    // The new tariff applies to the accumulated value of the goods just before its application.
    // In a cascading model, this means it applies to the `totalLandedCostPreNewTariff`.
    const newTariffImpactPerUnit = totalLandedCostPreNewTariff * newTariffRate;

    // --- Step 5: Calculate Total Landed Cost (Post-New Tariff) ---
    const totalLandedCostPostNewTariff = totalLandedCostPreNewTariff + newTariffImpactPerUnit;

    // --- Step 6: Calculate Percentage Cost Increase ---
    let percentageCostIncrease = 0;
    if (totalLandedCostPreNewTariff > 0) {
        percentageCostIncrease = ((totalLandedCostPostNewTariff - totalLandedCostPreNewTariff) / totalLandedCostPreNewTariff) * 100;
    } else if (newTariffImpactPerUnit > 0) {
        // If pre-tariff cost is 0 but there's a new tariff impact, indicate a significant increase.
        // A common practice for display is 100% or an even larger cap if the initial cost was zero but new cost is positive.
        percentageCostIncrease = 100; 
    }

    return {
        total_landed_cost_pre_tariff: Math.max(0, totalLandedCostPreNewTariff),
        new_tariff_impact_per_unit: Math.max(0, newTariffImpactPerUnit),
        total_landed_cost_post_tariff: Math.max(0, totalLandedCostPostNewTariff),
        percentage_cost_increase: Math.max(0, percentageCostIncrease)
    };
}
