function calculate(inputs) {
  const avgDailyAffectedRevenue = Number(inputs.average_daily_affected_revenue);
  const primaryDisruptionDays = Number(inputs.primary_disruption_days);
  const scResilienceFactor = Number(inputs.supply_chain_resilience_factor);
  const inventoryBufferDays = Number(inputs.inventory_buffer_days);
  const logisticsOverheadPercentage = Number(inputs.logistics_overhead_percentage);
  const expeditedShippingProportion = Number(inputs.expedited_shipping_proportion);
  const expeditedCostMultiplier = Number(inputs.expedited_cost_multiplier);
  const staffRecoveryHoursDaily = Number(inputs.staff_recovery_hours_daily);
  const avgHourlyPersonnelCost = Number(inputs.average_hourly_personnel_cost);
  const customerChurnRateIncreasePercentage = Number(inputs.customer_churn_rate_increase_percentage);

  // Input validation and edge cases
  if ([avgDailyAffectedRevenue, primaryDisruptionDays, scResilienceFactor, inventoryBufferDays, logisticsOverheadPercentage, 
       expeditedShippingProportion, expeditedCostMultiplier, staffRecoveryHoursDaily, avgHourlyPersonnelCost, 
       customerChurnRateIncreasePercentage].some(isNaN) || primaryDisruptionDays <= 0 || avgDailyAffectedRevenue < 0) {
    return {
      total_revenue_loss: 0,
      direct_logistics_disruption_costs: 0,
      expedited_and_recovery_costs: 0,
      potential_customer_churn_impact: 0,
      total_estimated_disruption_cost: 0,
      estimated_full_recovery_timeline: 0
    };
  }

  // 1. Estimated Direct Revenue Loss
  const total_revenue_loss = avgDailyAffectedRevenue * primaryDisruptionDays;

  // 2. Direct Logistics Disruption Costs
  // Base logistics cost for affected revenue days
  const baseLogisticsCost = avgDailyAffectedRevenue * (logisticsOverheadPercentage / 100) * primaryDisruptionDays;
  // Resilience factor impact: higher factor (more fragile) means higher cost multiplier
  const resilienceCostMultiplier = 1 + (scResilienceFactor - 1) * 0.15;
  const direct_logistics_disruption_costs = baseLogisticsCost * resilienceCostMultiplier;

  // 3. Expedited Shipping & Recovery Personnel Costs
  // Expedited shipping cost: based on a proportion of the *base* logistics cost that needs expediting, and the additional multiplier
  const expeditedShippingCost = baseLogisticsCost * (expeditedShippingProportion / 100) * (expeditedCostMultiplier - 1);

  // Personnel recovery cost: for primary disruption days, plus additional days if inventory buffer is insufficient
  const bufferDeficitDays = Math.max(0, primaryDisruptionDays - inventoryBufferDays);
  const recoveryPersonnelDays = primaryDisruptionDays + (bufferDeficitDays * 0.75); // 75% of buffer deficit days require extra recovery effort
  const personnelRecoveryCost = staffRecoveryHoursDaily * avgHourlyPersonnelCost * recoveryPersonnelDays;

  const expedited_and_recovery_costs = expeditedShippingCost + personnelRecoveryCost;

  // 4. Potential Customer Churn Financial Impact
  // This is a more complex estimate. We'll use the total revenue loss as a base,
  // apply the increased churn rate, and scale it by resilience factor (more fragile = higher impact on churn) 
  // and a heuristic factor (e.g., 50% of this estimated churn is directly attributable/realized from *this specific* disruption).
  const potential_customer_churn_impact = total_revenue_loss * (customerChurnRateIncreasePercentage / 100) * (scResilienceFactor / 5) * 0.5;

  // 5. Total Estimated Disruption Cost
  const total_estimated_disruption_cost = total_revenue_loss + direct_logistics_disruption_costs + expedited_and_recovery_costs + potential_customer_churn_impact;

  // 6. Estimated Full Recovery Timeline
  let estimated_full_recovery_timeline = primaryDisruptionDays;
  // Add days for buffer deficit, scaled by 1.5 for complexity
  estimated_full_recovery_timeline += Math.max(0, (primaryDisruptionDays - inventoryBufferDays) * 1.5);
  // Add days for resilience factor: more fragile = longer recovery
  estimated_full_recovery_timeline += (scResilienceFactor - 1) * 2;
  // Cap recovery timeline at a reasonable maximum, e.g., 90 days, to prevent extreme values
  estimated_full_recovery_timeline = Math.min(90, estimated_full_recovery_timeline);
  estimated_full_recovery_timeline = Math.max(0, estimated_full_recovery_timeline); // Ensure non-negative

  return {
    total_revenue_loss: total_revenue_loss,
    direct_logistics_disruption_costs: direct_logistics_disruption_costs,
    expedited_and_recovery_costs: expedited_and_recovery_costs,
    potential_customer_churn_impact: potential_customer_churn_impact,
    total_estimated_disruption_cost: total_estimated_disruption_cost,
    estimated_full_recovery_timeline: estimated_full_recovery_timeline
  };
}
