function calculate() {
    const projectAreaSqM = Math.max(0, inputs.projectAreaSqM);
    const initialCostConventionalPerSqM = Math.max(0, inputs.initialCostConventionalPerSqM);
    const initialCostSustainablePerSqM = Math.max(0, inputs.initialCostSustainablePerSqM);
    const projectLifecycleYears = Math.max(1, inputs.projectLifecycleYears);
    const annualOperationalAndMaintenanceDifference = inputs.annualOperationalAndMaintenanceDifference; // Sustainable - Conventional (can be negative for savings)
    const embodiedImpactCostConventionalPerSqM = Math.max(0, inputs.embodiedImpactCostConventionalPerSqM);
    const embodiedImpactCostSustainablePerSqM = Math.max(0, inputs.embodiedImpactCostSustainablePerSqM);
    const disposalCostConventionalPerSqM = Math.max(0, inputs.disposalCostConventionalPerSqM);
    const disposalCostSustainablePerSqM = Math.max(0, inputs.disposalCostSustainablePerSqM);
    const discountRatePercent = Math.max(0, inputs.discountRatePercent);

    const discountRate = discountRatePercent / 100;

    // Assume a baseline annual operational & maintenance cost for conventional materials if not provided. 
    // This is necessary to calculate 'total' LCC rather than just LCC differences.
    const assumedAnnualOpExMaintConventionalPerSqM = 15; // Placeholder: $/sq. m / year (a reasonable average for commercial buildings)

    // --- 1. Initial Costs --- 
    const initialCostConventional = projectAreaSqM * initialCostConventionalPerSqM;
    const initialCostSustainable = projectAreaSqM * initialCostSustainablePerSqM;
    const initialCostDifference = initialCostSustainable - initialCostConventional; // Positive if sustainable is more expensive initially

    // --- 2. Embodied Impact Costs --- 
    const embodiedImpactCostConventional = projectAreaSqM * embodiedImpactCostConventionalPerSqM;
    const embodiedImpactCostSustainable = projectAreaSqM * embodiedImpactCostSustainablePerSqM;

    // --- 3. Operational & Maintenance Costs (NPV of annual costs) ---
    const annualOpExMaintConventional = projectAreaSqM * assumedAnnualOpExMaintConventionalPerSqM;
    // Sustainable's annual OpEx & Maint is the conventional baseline plus the input difference
    const annualOpExMaintSustainable = annualOpExMaintConventional + annualOperationalAndMaintenanceDifference * projectAreaSqM;

    let npvOpExMaintConventional = 0;
    let npvOpExMaintSustainable = 0;

    if (discountRate > 0) {
        // NPV of an annuity: C * [1 - (1 + r)^-n] / r
        npvOpExMaintConventional = annualOpExMaintConventional * (1 - Math.pow(1 + discountRate, -projectLifecycleYears)) / discountRate;
        npvOpExMaintSustainable = annualOpExMaintSustainable * (1 - Math.pow(1 + discountRate, -projectLifecycleYears)) / discountRate;
    } else { // No discounting, simple sum over years
        npvOpExMaintConventional = annualOpExMaintConventional * projectLifecycleYears;
        npvOpExMaintSustainable = annualOpExMaintSustainable * projectLifecycleYears;
    }

    // --- 4. Disposal Costs (End-of-Life, discounted) ---
    const disposalCostConventionalTotal = projectAreaSqM * disposalCostConventionalPerSqM;
    const disposalCostSustainableTotal = projectAreaSqM * disposalCostSustainablePerSqM;

    let npvDisposalCostConventional = 0;
    let npvDisposalCostSustainable = 0;

    if (projectLifecycleYears > 0 && discountRate > 0) {
        // NPV of a single future payment: F / (1 + r)^n
        npvDisposalCostConventional = disposalCostConventionalTotal / Math.pow(1 + discountRate, projectLifecycleYears);
        npvDisposalCostSustainable = disposalCostSustainableTotal / Math.pow(1 + discountRate, projectLifecycleYears);
    } else { // No discounting or zero lifecycle
        npvDisposalCostConventional = disposalCostConventionalTotal;
        npvDisposalCostSustainable = disposalCostSustainableTotal;
    }

    // --- 5. Total Lifecycle Costs (LCC) ---
    const totalLCCConventional = initialCostConventional + embodiedImpactCostConventional + npvOpExMaintConventional + npvDisposalCostConventional;
    const totalLCCSustainable = initialCostSustainable + embodiedImpactCostSustainable + npvOpExMaintSustainable + npvDisposalCostSustainable;

    const netLCCBenefitSustainable = totalLCCConventional - totalLCCSustainable; // Positive if sustainable is cheaper overall

    // --- 6. Payback Period ---
    let paybackPeriodYears = "N/A";
    const initialInvestmentDelta = initialCostSustainable - initialCostConventional; // Positive if sustainable costs more initially

    if (initialInvestmentDelta <= 0) {
        paybackPeriodYears = "Immediate (Sustainable is initially cheaper or same cost)";
    } else {
        // Annual net operational and maintenance savings (positive value means savings)
        const annualNetOperationalSavings = -annualOperationalAndMaintenanceDifference * projectAreaSqM; 

        if (annualNetOperationalSavings > 0) {
            let calculatedPayback = initialInvestmentDelta / annualNetOperationalSavings;
            if (calculatedPayback > projectLifecycleYears) {
                paybackPeriodYears = `> ${projectLifecycleYears} years (No payback within project lifecycle)`;
            } else if (calculatedPayback <= 0 || !isFinite(calculatedPayback)) { 
                paybackPeriodYears = "N/A";
            } else {
                paybackPeriodYears = parseFloat(calculatedPayback.toFixed(2));
            }
        } else {
            paybackPeriodYears = "N/A (No annual operational/maintenance savings to offset initial investment)";
        }
    }

    // --- 7. Net Present Value of Choosing Sustainable ---
    // This represents the sum of all discounted differences (Conventional - Sustainable)
    const npvInitialDiff = initialCostConventional - initialCostSustainable;
    const npvEmbodiedDiff = embodiedImpactCostConventional - embodiedImpactCostSustainable;
    const npvOpExMaintDiff = npvOpExMaintConventional - npvOpExMaintSustainable;
    const npvDisposalDiff = npvDisposalCostConventional - npvDisposalCostSustainable;

    const npvOfDifference = npvInitialDiff + npvEmbodiedDiff + npvOpExMaintDiff + npvDisposalDiff;

    return {
        totalLCCConventional: totalLCCConventional,
        totalLCCSustainable: totalLCCSustainable,
        netLCCBenefitSustainable: netLCCBenefitSustainable,
        initialCostDifference: initialCostDifference,
        paybackPeriodYears: paybackPeriodYears,
        npvOfDifference: npvOfDifference
    };
}
