(function(inputs) {
  const materialPurchaseCost = inputs.materialPurchaseCost || 0;
  const materialQuantity = inputs.materialQuantity || 0;
  const transportationCostPerUnit = inputs.transportationCostPerUnit || 0;
  const installationCostPerUnit = inputs.installationCostPerUnit || 0;
  let expectedLifespanYears = inputs.expectedLifespanYears || 0;
  const annualMaintenanceCostPerUnit = inputs.annualMaintenanceCostPerUnit || 0;
  const energyEfficiencyImpactPerUnitPerYear = inputs.energyEfficiencyImpactPerUnitPerYear || 0;
  const disposalCostPerUnit = inputs.disposalCostPerUnit || 0;
  const salvageValuePerUnit = inputs.salvageValuePerUnit || 0;

  // Ensure lifespan is at least 1 year to avoid division by zero for annualized cost
  if (expectedLifespanYears < 1) {
    expectedLifespanYears = 1; // Minimum for calculation purposes
  }

  // Phase 1: Initial Investment Cost
  const totalMaterialCost = materialPurchaseCost * materialQuantity;
  const totalTransportationCost = transportationCostPerUnit * materialQuantity;
  const totalInstallationCost = installationCostPerUnit * materialQuantity;
  const initialInvestmentCost = totalMaterialCost + totalTransportationCost + totalInstallationCost;

  // Phase 2: Operational Costs over Lifespan
  const totalMaintenanceCostOverLifespan = annualMaintenanceCostPerUnit * materialQuantity * expectedLifespanYears;
  // energyEfficiencyImpactPerUnitPerYear is negative for savings, positive for costs
  const totalEnergyImpactCostOverLifespan = energyEfficiencyImpactPerUnitPerYear * materialQuantity * expectedLifespanYears;
  const totalOperationalCost = totalMaintenanceCostOverLifespan + totalEnergyImpactCostOverLifespan;

  // Phase 3: End-of-Life Costs
  const totalDisposalCost = disposalCostPerUnit * materialQuantity;
  const totalSalvageValue = salvageValuePerUnit * materialQuantity;
  const endOfLifeNetCost = totalDisposalCost - totalSalvageValue; // Salvage value reduces net cost

  // Total Lifecycle Cost
  const totalLifecycleCost = initialInvestmentCost + totalOperationalCost + endOfLifeNetCost;

  // Annualized Lifecycle Cost
  const annualizedLifecycleCost = totalLifecycleCost / expectedLifespanYears;

  return {
    totalLifecycleCost: totalLifecycleCost,
    annualizedLifecycleCost: annualizedLifecycleCost,
    initialInvestmentCost: initialInvestmentCost,
    totalOperationalCost: totalOperationalCost,
    endOfLifeNetCost: endOfLifeNetCost
  };
})
