function calculate({
  initialPortfolioValue,
  annualReturnRate,
  timeHorizonYears,
  portfolioESGIntegrationLevel,
  averageCarbonIntensityPerMillionRevenue,
  averageWaterWithdrawalPerMillionRevenue,
  averageWasteGenerationPerMillionRevenue,
  socialPerformanceScore,
  governancePerformanceScore
}) {
  const results = {};

  // Input Validation & Defaults
  initialPortfolioValue = Math.max(0, parseFloat(initialPortfolioValue) || 0);
  annualReturnRate = Math.max(0, Math.min(20, parseFloat(annualReturnRate) || 0));
  timeHorizonYears = Math.max(1, Math.min(50, parseFloat(timeHorizonYears) || 1));
  portfolioESGIntegrationLevel = Math.max(0, Math.min(100, parseFloat(portfolioESGIntegrationLevel) || 0));
  averageCarbonIntensityPerMillionRevenue = Math.max(0, parseFloat(averageCarbonIntensityPerMillionRevenue) || 0);
  averageWaterWithdrawalPerMillionRevenue = Math.max(0, parseFloat(averageWaterWithdrawalPerMillionRevenue) || 0);
  averageWasteGenerationPerMillionRevenue = Math.max(0, parseFloat(averageWasteGenerationPerMillionRevenue) || 0);
  socialPerformanceScore = Math.max(0, Math.min(100, parseFloat(socialPerformanceScore) || 0));
  governancePerformanceScore = Math.max(0, Math.min(100, parseFloat(governancePerformanceScore) || 0));

  // Define baseline intensities for a 'non-ESG' portfolio (illustrative benchmarks)
  const baselineCarbonIntensity = 250; // tonnes CO2e per $M revenue
  const baselineWaterIntensity = 1500; // m^3 per $M revenue
  const baselineWasteIntensity = 50; // tonnes per $M revenue

  // 1. Calculate Environmental Impact Savings
  // We use the initial portfolio value for a simplified impact calculation base, assuming consistent operations.
  // For a more complex model, the impact could be cumulative over projected value each year.
  // For simplicity and clarity in a single output, we relate it to the projected value (pre-ESG adjusted).

  // Temporary financial projection for impact calculation, assumes no ESG adjustment yet.
  const projectedFinancialValue_pre_esg = initialPortfolioValue * Math.pow(1 + annualReturnRate / 100, timeHorizonYears);

  const valueFactor = projectedFinancialValue_pre_esg / 1_000_000; // Value in millions

  results.estimatedCarbonImpactSavings = (baselineCarbonIntensity - averageCarbonIntensityPerMillionRevenue) * valueFactor;
  results.estimatedWaterImpactSavings = (baselineWaterIntensity - averageWaterWithdrawalPerMillionRevenue) * valueFactor;
  results.estimatedWasteImpactSavings = (baselineWasteGenerationPerMillionRevenue - averageWasteGenerationPerMillionRevenue) * valueFactor;

  // If initial value is 0, all environmental impacts are 0.
  if (initialPortfolioValue === 0) {
    results.estimatedCarbonImpactSavings = 0;
    results.estimatedWaterImpactSavings = 0;
    results.estimatedWasteImpactSavings = 0;
  }

  // 2. Social & Governance Scores (direct output of user input, representing portfolio's standing)
  results.overallSocialImpactScore = socialPerformanceScore;
  results.overallGovernanceImpactScore = governancePerformanceScore;

  // 3. Calculate Total ESG Performance Score (Composite E, S, G)
  // Normalize environmental intensities to a 0-100 score, where lower intensity means a higher score.
  // Max(0, ...) to prevent negative scores if current intensity is much higher than baseline.
  const normalizedCarbonScore = Math.max(0, 100 * (1 - (averageCarbonIntensityPerMillionRevenue / baselineCarbonIntensity)));
  const normalizedWaterScore = Math.max(0, 100 * (1 - (averageWaterWithdrawalPerMillionRevenue / baselineWaterIntensity)));
  const normalizedWasteScore = Math.max(0, 100 * (1 - (averageWasteGenerationPerMillionRevenue / baselineWasteIntensity)));

  // Environmental composite with weighting
  const environmentalCompositeScore = (normalizedCarbonScore * 0.5 + normalizedWaterScore * 0.3 + normalizedWasteScore * 0.2);

  // Total ESG Performance Score, weighted and scaled by ESG integration level
  results.totalESGPerformanceScore = 
    ((environmentalCompositeScore * 0.4) + 
     (socialPerformanceScore * 0.3) + 
     (governancePerformanceScore * 0.3)) * 
    (portfolioESGIntegrationLevel / 100);
  results.totalESGPerformanceScore = Math.max(0, Math.min(100, results.totalESGPerformanceScore)); // Cap between 0 and 100

  // 4. Calculate ESG-Adjusted Annual Return Rate
  // ESG performance can potentially impact financial returns. A score above 50 (mid-point) suggests positive influence,
  // below 50 suggests potential drag or higher risk. The 'annualReturnRate / 5' sets the magnitude of this adjustment.
  // E.g., if annualReturnRate is 10%, max adjustment is +/- 2% (20% of 10%).
  const esgPerformanceDifferential = (results.totalESGPerformanceScore - 50) / 100; // Range -0.5 to 0.5
  const esgReturnAdjustment = esgPerformanceDifferential * (annualReturnRate / 5);

  results.esgAdjustedAnnualReturn = annualReturnRate + esgReturnAdjustment;
  // Ensure ESG-adjusted return doesn't go below a reasonable floor, e.g., 0% or slightly negative if appropriate.
  results.esgAdjustedAnnualReturn = Math.max(annualReturnRate * 0.5, results.esgAdjustedAnnualReturn); // Cap at 50% of initial return rate for floor
  results.esgAdjustedAnnualReturn = Math.min(annualReturnRate * 1.5, results.esgAdjustedAnnualReturn); // Cap at 150% of initial return rate for ceiling

  // 5. Final Projected Financial Value (using ESG-adjusted return)
  results.projectedFinancialValue = initialPortfolioValue * Math.pow(1 + results.esgAdjustedAnnualReturn / 100, timeHorizonYears);

  // If initialPortfolioValue is 0, then projected value is also 0
  if (initialPortfolioValue === 0) {
    results.projectedFinancialValue = 0;
  }

  return results;
}
