const materialData = {
    "Reinforced Concrete": { embodiedCarbonFactor: 350, density: 2400 }, 
    "Virgin Steel": { embodiedCarbonFactor: 7500, density: 7850 },
    "Conventional Brick": { embodiedCarbonFactor: 400, density: 1800 },
    "Cross-Laminated Timber (CLT)": { embodiedCarbonFactor: 50, density: 500 }, 
    "Hempcrete": { embodiedCarbonFactor: 20, density: 350 },
    "Recycled Steel": { embodiedCarbonFactor: 2000, density: 7850 },
    "Engineered Bamboo": { embodiedCarbonFactor: 40, density: 700 }
};

const transportEmissionFactors = { 
    "Road": 0.1, 
    "Rail": 0.03,
    "Sea": 0.01
};

const volume = parseFloat(inputs.material_volume_m3);
const transportDistance = parseFloat(inputs.transport_distance_km);
const sustainableMaterial = inputs.sustainable_material_type;
const traditionalMaterial = inputs.traditional_material_type;
const transportMode = inputs.transport_mode;
const projectLifespan = parseFloat(inputs.project_lifespan_years); // Not directly used in current A1-A4 calculation, but included for robustness and potential future lifecycle extensions.

if (isNaN(volume) || volume <= 0 || isNaN(transportDistance) || transportDistance < 0 || isNaN(projectLifespan) || projectLifespan <= 0) {
    return {
        embodied_carbon_sustainable_tco2e: 0,
        embodied_carbon_traditional_tco2e: 0,
        carbon_emission_savings_tco2e: 0,
        carbon_emission_savings_percentage: 0,
        error: "Please enter valid positive numbers for material volume, transport distance, and project lifespan."
    };
}

const getMaterialImpact = (materialName) => {
    const data = materialData[materialName];
    if (!data) {
        throw new Error(`Material data not found for ${materialName}. Please select a valid material.`);
    }

    const production_kgco2e = data.embodiedCarbonFactor * volume;

    const totalMass_tonnes = (volume * data.density) / 1000;

    const transportFactor = transportEmissionFactors[transportMode];
    if (!transportFactor) {
        throw new Error(`Transport mode factor not found for ${transportMode}. Please select a valid transport mode.`);
    }
    const transport_kgco2e = totalMass_tonnes * transportDistance * transportFactor;

    return production_kgco2e + transport_kgco2e;
};

let embodiedCarbonSustainable_kgco2e = 0;
let embodiedCarbonTraditional_kgco2e = 0;

try {
    embodiedCarbonSustainable_kgco2e = getMaterialImpact(sustainableMaterial);
    embodiedCarbonTraditional_kgco2e = getMaterialImpact(traditionalMaterial);
} catch (e) {
    return {
        embodied_carbon_sustainable_tco2e: 0,
        embodied_carbon_traditional_tco2e: 0,
        carbon_emission_savings_tco2e: 0,
        carbon_emission_savings_percentage: 0,
        error: e.message
    };
}

const embodiedCarbonSustainable_tco2e = embodiedCarbonSustainable_kgco2e / 1000;
const embodiedCarbonTraditional_tco2e = embodiedCarbonTraditional_kgco2e / 1000;

const carbonEmissionSavings_tco2e = embodiedCarbonTraditional_tco2e - embodiedCarbonSustainable_tco2e;

let carbonEmissionSavingsPercentage = 0;
if (embodiedCarbonTraditional_tco2e > 0) {
    carbonEmissionSavingsPercentage = (carbonEmissionSavings_tco2e / embodiedCarbonTraditional_tco2e) * 100;
} else if (embodiedCarbonTraditional_tco2e === 0 && embodiedCarbonSustainable_tco2e === 0) {
    carbonEmissionSavingsPercentage = 0;
} else if (embodiedCarbonTraditional_tco2e === 0 && embodiedCarbonSustainable_tco2e > 0) {
    carbonEmissionSavingsPercentage = -100; 
}

return {
    embodied_carbon_sustainable_tco2e: embodiedCarbonSustainable_tco2e,
    embodied_carbon_traditional_tco2e: embodiedCarbonTraditional_tco2e,
    carbon_emission_savings_tco2e: carbonEmissionSavings_tco2e,
    carbon_emission_savings_percentage: carbonEmissionSavingsPercentage
};
