function calculateLifeCycleCost(material_quantity, project_duration, discount_rate, acquisition_cost, installation_cost, maintenance_cost, disposal_cost, energy_savings = 0, carbon_credits = 0) {
  const q = Math.max(0, material_quantity);
  const ls = Math.max(1, project_duration); 
  const dr = Math.max(0, discount_rate) / 100;

  // Initial Costs
  const initialCost = (acquisition_cost + installation_cost) * q;

  // Annual Costs/Benefits (Maintenance, Energy Savings, Carbon Credits)
  const netAnnualCostBenefit = (maintenance_cost - energy_savings - carbon_credits) * q;

  let npvAnnualCosts = 0;
  if (netAnnualCostBenefit !== 0) {
    if (dr === 0) {
      // Simple sum if no discount rate
      npvAnnualCosts = netAnnualCostBenefit * ls;
    } else {
      // NPV of an annuity (assuming costs occur at year-end)
      npvAnnualCosts = netAnnualCostBenefit * ((1 - Math.pow(1 + dr, -ls)) / dr);
    }
  }

  // End-of-Life Cost
  const endOfLifeCost = disposal_cost * q;

  // Total LCC
  return initialCost + npvAnnualCosts + endOfLifeCost;
}

const material_quantity = inputs.material_quantity;
const project_duration = inputs.project_duration;
const discount_rate = inputs.discount_rate;

// Calculate Sustainable Material LCC
const sustainable_lcc = calculateLifeCycleCost(
  material_quantity,
  project_duration,
  discount_rate,
  inputs.sustainable_acquisition_cost,
  inputs.sustainable_installation_cost,
  inputs.sustainable_maintenance_cost,
  inputs.sustainable_disposal_cost,
  inputs.sustainable_energy_savings || 0,
  inputs.sustainable_carbon_credits || 0
);

// Calculate Traditional Material LCC
const traditional_lcc = calculateLifeCycleCost(
  material_quantity,
  project_duration,
  discount_rate,
  inputs.traditional_acquisition_cost,
  inputs.traditional_installation_cost,
  inputs.traditional_maintenance_cost,
  inputs.traditional_disposal_cost
);

const cost_difference = sustainable_lcc - traditional_lcc;

// Calculate annual net costs/benefits per material type
const annual_net_cost_sustainable = (inputs.sustainable_maintenance_cost - (inputs.sustainable_energy_savings || 0) - (inputs.sustainable_carbon_credits || 0)) * material_quantity;
const annual_net_cost_traditional = inputs.traditional_maintenance_cost * material_quantity;

// Payback Period Calculation
let payback_period_output = 'N/A';
const initial_diff_sustainable_over_traditional_per_sqm = (inputs.sustainable_acquisition_cost + inputs.sustainable_installation_cost) - (inputs.traditional_acquisition_cost + inputs.traditional_installation_cost);
const total_initial_diff = initial_diff_sustainable_over_traditional_per_sqm * material_quantity;
const annual_net_savings_sustainable_over_traditional = annual_net_cost_traditional - annual_net_cost_sustainable;

if (total_initial_diff <= 0) {
  payback_period_output = 0;
} else if (annual_net_savings_sustainable_over_traditional > 0) {
  payback_period_output = Math.min(project_duration, Math.max(0, total_initial_diff / annual_net_savings_sustainable_over_traditional));
} else {
  payback_period_output = 'Never';
}

// ROI Calculation: (Savings over Traditional LCC) / (Traditional LCC) * 100
let roi_sustainable = 0;
if (traditional_lcc > 0) {
  roi_sustainable = ((traditional_lcc - sustainable_lcc) / traditional_lcc) * 100;
} else if (sustainable_lcc < 0 && traditional_lcc <= 0) { // e.g., if traditional LCC is negative due to extreme factors, but sustainable is even better (more negative)
    roi_sustainable = Infinity;
} else if (sustainable_lcc < traditional_lcc) { // If traditional_lcc is 0 or negative, but sustainable is less (e.g. less negative)
    roi_sustainable = Infinity;
} else {
  roi_sustainable = 0; // No ROI or traditional is better or equal, and traditional_lcc is not positive.
}

return {
  sustainable_lcc: sustainable_lcc,
  traditional_lcc: traditional_lcc,
  cost_difference: cost_difference,
  annual_net_cost_sustainable: annual_net_cost_sustainable,
  annual_net_cost_traditional: annual_net_cost_traditional,
  payback_period: typeof payback_period_output === 'number' ? parseFloat(payback_period_output.toFixed(2)) : payback_period_output,
  roi_sustainable: parseFloat(roi_sustainable.toFixed(2))
};
