function calculate(inputs) {
    const {
        incoming_temp,
        output_temp,
        num_showers,
        num_sinks,
        num_dishwashers,
        shower_gpm,
        sink_gpm,
        dishwasher_gpm
    } = inputs;

    // Cap the desired output temperature to a reasonable max for safety/efficiency (e.g., 140°F)
    const effective_output_temperature = Math.min(output_temp, 140);
    // Ensure temperature rise is not negative
    const required_temperature_rise = Math.max(0, effective_output_temperature - incoming_temp);

    const total_required_flow_rate =
        (num_showers * shower_gpm) +
        (num_sinks * sink_gpm) +
        (num_dishwashers * dishwasher_gpm);

    // BTU/hr = GPM * 500 * ΔT (where 500 is approx. (8.34 lbs/gal * 60 min/hr * 1 BTU/lb°F))
    const estimated_btu_needed = total_required_flow_rate * 500 * required_temperature_rise;

    // Recommend a heater with a buffer (e.g., 15% more capacity) for peak demand and future needs
    const recommended_heater_capacity = estimated_btu_needed * 1.15;

    return {
        total_required_flow_rate: total_required_flow_rate,
        required_temperature_rise: required_temperature_rise,
        estimated_btu_needed: estimated_btu_needed,
        recommended_heater_capacity: recommended_heater_capacity
    };
}
