const { age, rhr } = inputs;

// Input validation
if (age <= 0 || age > 120 || !Number.isInteger(age)) {
    throw new Error("Please enter a realistic age between 1 and 120.");
}
if (rhr <= 20 || rhr >= 200 || !Number.isInteger(rhr)) {
    throw new Error("Please enter a realistic resting heart rate between 20 and 200 BPM.");
}

// Karvonen Formula calculation
// Step 1: Estimate Maximum Heart Rate (MHR) using Fox, Naughton and Haskell formula (220 - age)
const mhr = 220 - age;

// Step 2: Calculate Heart Rate Reserve (HRR)
const hrr = mhr - rhr;

// Ensure HRR is not negative (e.g., if RHR is very high, though validation above should prevent most)
const effectiveHrr = Math.max(0, hrr);

// Step 3: Calculate Target Heart Rate (THR) for different zones
// THR = (HRR * % Intensity) + RHR

// Fat-Burning Zone (50-70% of HRR)
const fat_burn_min = Math.round((effectiveHrr * 0.50) + rhr);
const fat_burn_max = Math.round((effectiveHrr * 0.70) + rhr);

// Cardio Zone (70-85% of HRR)
const cardio_min = Math.round((effectiveHrr * 0.70) + rhr);
const cardio_max = Math.round((effectiveHrr * 0.85) + rhr);

// Peak Zone (85-95% of HRR, capped for safety)
const peak_min = Math.round((effectiveHrr * 0.85) + rhr);
const peak_max_calculated = Math.round((effectiveHrr * 0.95) + rhr); 

// Cap output values at MHR if they exceed it due to rounding or edge cases
const cappedPeakMax = Math.min(peak_max_calculated, mhr);

return {
    mhr: Math.round(mhr),
    hrr: Math.round(effectiveHrr),
    fat_burn_min: Math.max(0, fat_burn_min), 
    fat_burn_max: Math.max(0, fat_burn_max),
    cardio_min: Math.max(0, cardio_min),
    cardio_max: Math.max(0, cardio_max),
    peak_min: Math.max(0, peak_min),
    peak_max: Math.max(0, cappedPeakMax)
};
