function calculate() {
  const age = parseFloat(inputs.age);
  const restingHeartRate = parseFloat(inputs.restingHeartRate);
  let maxHR = parseFloat(inputs.estimatedMaxHeartRate);

  if (isNaN(maxHR) || maxHR === 0) {
    maxHR = 220 - age;
  }

  const heartRateReserve = maxHR - restingHeartRate;

  // Karvonen Formula: Target HR = ((Max HR - Resting HR) * % Intensity) + Resting HR
  const calculateZone = (percentage) => {
    return Math.round((heartRateReserve * percentage) + restingHeartRate);
  };

  // Define intensity percentages for zones based on Heart Rate Reserve (HRR)
  // Fat Burning: 60-70% of HRR
  // Aerobic (Cardio): 70-80% of HRR
  // Anaerobic (Peak): 80-90% of HRR

  const fatBurningLowerBpm = calculateZone(0.60);
  const fatBurningUpperBpm = calculateZone(0.70);

  const aerobicLowerBpm = calculateZone(0.70);
  const aerobicUpperBpm = calculateZone(0.80);

  const anaerobicLowerBpm = calculateZone(0.80);
  const anaerobicUpperBpm = calculateZone(0.90);

  return {
    estimatedMaxHR: Math.round(maxHR),
    fatBurningLowerBpm,
    fatBurningUpperBpm,
    aerobicLowerBpm,
    aerobicUpperBpm,
    anaerobicLowerBpm,
    anaerobicUpperBpm
  };
}
