function calculate(inputs) {
  const claimedAmount = Math.max(0, inputs.claimedAmount);
  const complexityLevel = Math.max(1, Math.min(5, inputs.complexityLevel));
  const disputeStage = Math.max(1, Math.min(4, inputs.disputeStage));
  const hourlyLegalFee = Math.max(0, inputs.hourlyLegalFee);
  const hourlyAccountingFee = Math.max(0, inputs.hourlyAccountingFee);
  const irsSuccessRate = Math.max(0, Math.min(100, inputs.irsSuccessRate));
  const yearsDisputed = Math.max(1, inputs.yearsDisputed);
  const penaltyType = Math.max(1, Math.min(3, inputs.penaltyType));

  // --- Constants and Multipliers ---

  // Complexity multipliers for hours
  const complexityMultipliers = {
    1: 1.0, // Simple
    2: 1.25, // Moderate
    3: 1.75, // Complex
    4: 2.5, // Very Complex
    5: 3.5  // Highly Complex
  };

  // Dispute stage multipliers for legal hours
  const legalStageMultipliers = {
    1: 1.0, // Audit
    2: 1.5, // Appeals
    3: 2.5, // Tax Court
    4: 4.0  // Federal Court
  };

  // Dispute stage multipliers for accounting hours
  const accountingStageMultipliers = {
    1: 1.0, // Audit
    2: 1.3, // Appeals
    3: 2.0, // Tax Court
    4: 3.0  // Federal Court
  };

  // Base hours per $10,000 of claimed amount (for simple audit, complexity 1)
  const baseLegalHoursPer10k = 5; 
  const baseAccountingHoursPer10k = 3;

  // Minimum professional hours regardless of claimed amount (for simple audit, complexity 1)
  const minLegalHours = 20;
  const minAccountingHours = 10;

  // Max professional fees as a percentage of claimed amount (cap)
  const maxLegalFeePercentage = 0.60; // 60% of claimed amount
  const maxAccountingFeePercentage = 0.30; // 30% of claimed amount

  // Assumed IRS Annual Interest Rate (simplified for estimation)
  const irsAnnualInterestRate = 0.05; // 5%

  // Penalty percentages
  let penaltyPercentage;
  switch (penaltyType) {
    case 1: // Accuracy-Related (20% of underpayment)
      penaltyPercentage = 0.20;
      break;
    case 2: // Fraud (75% of underpayment)
      penaltyPercentage = 0.75;
      break;
    case 3: // Failure-to-File/Pay (simplified to 25% for calculation, can be 5% per month up to 25% for failure-to-pay or 5% per month up to 25% for failure-to-file)
      penaltyPercentage = 0.25;
      break;
    default:
      penaltyPercentage = 0.20; // Default to accuracy-related
  }

  // --- Calculations ---

  // 1. Estimated Legal Costs
  let estimatedLegalHours = (claimedAmount / 10000) * baseLegalHoursPer10k;
  estimatedLegalHours *= complexityMultipliers[complexityLevel];
  estimatedLegalHours *= legalStageMultipliers[disputeStage];
  estimatedLegalHours = Math.max(minLegalHours * complexityMultipliers[complexityLevel] * legalStageMultipliers[disputeStage], estimatedLegalHours); // Apply stage and complexity to min hours
  let estimatedLegalCosts = estimatedLegalHours * hourlyLegalFee;
  estimatedLegalCosts = Math.min(estimatedLegalCosts, claimedAmount * maxLegalFeePercentage); // Cap legal fees

  // 2. Estimated Accounting Costs
  let estimatedAccountingHours = (claimedAmount / 10000) * baseAccountingHoursPer10k;
  estimatedAccountingHours *= complexityMultipliers[complexityLevel];
  estimatedAccountingHours *= accountingStageMultipliers[disputeStage];
  estimatedAccountingHours = Math.max(minAccountingHours * complexityMultipliers[complexityLevel] * accountingStageMultipliers[disputeStage], estimatedAccountingHours); // Apply stage and complexity to min hours
  let estimatedAccountingCosts = estimatedAccountingHours * hourlyAccountingFee;
  estimatedAccountingCosts = Math.min(estimatedAccountingCosts, claimedAmount * maxAccountingFeePercentage); // Cap accounting fees

  // 3. Estimated Penalty Costs
  const potentialPenaltyAmount = claimedAmount * penaltyPercentage;
  // Estimated actual penalty paid depends on IRS success rate
  let estimatedPenaltyCosts = potentialPenaltyAmount * (irsSuccessRate / 100);

  // 4. Estimated Interest Costs
  // Interest is calculated on the claimed amount for the disputed years, if the IRS prevails
  let estimatedInterestCosts = claimedAmount * irsAnnualInterestRate * yearsDisputed;
  // Estimated actual interest paid depends on IRS success rate
  estimatedInterestCosts *= (irsSuccessRate / 100);

  // 5. Total Estimated Cost
  const totalEstimatedCost = estimatedLegalCosts + estimatedAccountingCosts + estimatedPenaltyCosts + estimatedInterestCosts;

  return {
    estimatedLegalCosts: estimatedLegalCosts,
    estimatedAccountingCosts: estimatedAccountingCosts,
    estimatedPenaltyCosts: estimatedPenaltyCosts,
    estimatedInterestCosts: estimatedInterestCosts,
    totalEstimatedCost: totalEstimatedCost
  };
}
