const taxExemptYield = inputs.taxExemptYield; // Input is already a percentage
const federalTaxRate = inputs.federalTaxRate; // Input is already a percentage
const stateTaxRate = inputs.stateTaxRate; // Input is already a percentage

const federalRateDecimal = federalTaxRate / 100;
const stateRateDecimal = stateTaxRate / 100;

// Calculate the effective combined marginal tax rate, assuming state tax is deductible federally
const effectiveCombinedTaxRateDecimal = federalRateDecimal + (stateRateDecimal * (1 - federalRateDecimal));

let taxableEquivalentYield = NaN; // Default to NaN in case of invalid rates

// Denominator for the calculation
const denominator = 1 - effectiveCombinedTaxRateDecimal;

if (denominator > 0) {
    taxableEquivalentYield = (taxExemptYield / denominator);
} else if (taxExemptYield > 0) {
    // If denominator is 0 or negative, it implies tax rate is 100% or more.
    // In this scenario, any positive tax-exempt yield is infinitely better.
    taxableEquivalentYield = Infinity;
} else {
    // If tax-exempt yield is 0, then equivalent is 0 regardless of rate
    taxableEquivalentYield = 0;
}

// The total tax rate as a percentage, capped at 100%.
const totalTaxRatePercentage = Math.max(0, Math.min(100, effectiveCombinedTaxRateDecimal * 100));

return {
    taxableEquivalentYield: taxableEquivalentYield,
    totalTaxRate: totalTaxRatePercentage
};
