function calculate() {
    const inputs = data;

    // Constants and base factors
    const BASE_LOSS_PER_DAY = 0.15; // Weeks of learning loss per day of strike in core subjects
    const MAX_MITIGATION_REDUCTION = 0.7; // Max 70% reduction even if 100% effective
    const MIN_MITIGATION_IMPACT_FACTOR = 0.3; // Min 30% impact even with perfect mitigation
    const LONG_TERM_BASE_FACTOR = 0.015; // Initial % point reduction per week of loss
    const MAX_LONG_TERM_REDUCTION = 10; // Cap at 10% point reduction
    const EQUITY_GAP_BASE_WORSENING_FACTOR = 0.5; // Base points increase in gap per week of loss
    const MAX_EQUITY_GAP_WORSENING = 15; // Cap at 15% increase in gap

    // 1. Calculate Core Learning Loss Multipliers
    let coreSubjectImpactMultiplier = 1 + (inputs.affectedCoreSubjects / 100 * 0.4); // Up to 40% more if 100% core subjects affected
    let vulnerableStudentAmplifier = 1 + (inputs.vulnerableStudentPercentage / 100 * 0.6); // Up to 60% more impact for 100% vulnerable students
    let priorAchievementSensitivity = (6 - inputs.averagePriorAchievementScore) / 5; // Scale: 1 (score 1) to 0.2 (score 5). Lower score = higher sensitivity.

    let gradeLevelFactor = 1.0;
    if (inputs.gradeLevelCategory === "Elementary") gradeLevelFactor = 1.25;
    else if (inputs.gradeLevelCategory === "Middle") gradeLevelFactor = 1.0;
    else if (inputs.gradeLevelCategory === "High") gradeLevelFactor = 0.75;

    let districtSizeModifier = 1.0;
    if (inputs.districtSizeCategory === "Small") districtSizeModifier = 0.9;
    else if (inputs.districtSizeCategory === "Large") districtSizeModifier = 1.1;

    // 2. Apply Mitigation Strategies
    let mitigationReductionFactor = 1 - (inputs.mitigationEffectiveness / 100 * MAX_MITIGATION_REDUCTION);
    mitigationReductionFactor = Math.max(MIN_MITIGATION_IMPACT_FACTOR, mitigationReductionFactor); // Ensure at least min impact remains

    // 3. Estimate Learning Loss in Weeks
    let estimatedLearningLossWeeks = inputs.strikeDurationDays * BASE_LOSS_PER_DAY;
    estimatedLearningLossWeeks *= coreSubjectImpactMultiplier;
    estimatedLearningLossWeeks *= vulnerableStudentAmplifier;
    estimatedLearningLossWeeks *= priorAchievementSensitivity;
    estimatedLearningLossWeeks *= gradeLevelFactor;
    estimatedLearningLossWeeks *= districtSizeModifier;
    estimatedLearningLossWeeks *= mitigationReductionFactor;

    // Cap estimated learning loss to a realistic maximum relative to strike duration
    estimatedLearningLossWeeks = Math.min(estimatedLearningLossWeeks, inputs.strikeDurationDays * 0.3); // Max 0.3 weeks of loss per day of strike

    // 4. Calculate Long-Term Achievement Reduction
    let longTermAchievementReduction = estimatedLearningLossWeeks * LONG_TERM_BASE_FACTOR;
    // Vulnerable students and lower prior achievement amplify long-term effects more significantly
    longTermAchievementReduction *= (1 + (vulnerableStudentAmplifier - 1) * 0.7);
    longTermAchievementReduction *= (1 + (priorAchievementSensitivity - 0.2) * 0.4);

    // Post-strike support reduces long-term impact
    let postStrikeSupportReductionFactor = 1 - (inputs.postStrikeSupportLevel / 5 * 0.4); // Up to 40% reduction for level 5 support
    longTermAchievementReduction *= postStrikeSupportReductionFactor;

    longTermAchievementReduction = Math.min(longTermAchievementReduction, MAX_LONG_TERM_REDUCTION); // Apply cap

    // 5. Calculate Equity Gap Worsening
    let equityGapWorsening = estimatedLearningLossWeeks * EQUITY_GAP_BASE_WORSENING_FACTOR;
    // Equity gap worsening is highly sensitive to vulnerable student population
    equityGapWorsening *= (inputs.vulnerableStudentPercentage / 100 * 1.5 + 0.5); // Amplified by vulnerable student percentage
    equityGapWorsening *= (1 + (gradeLevelFactor - 1) * 0.8); // Elementary has higher equity gap impact
    equityGapWorsening *= (1 + (priorAchievementSensitivity - 0.2) * 0.6); // Lower prior achievement means higher gap worsening

    equityGapWorsening *= postStrikeSupportReductionFactor; // Post-strike support also helps here.

    equityGapWorsening = Math.min(equityGapWorsening, MAX_EQUITY_GAP_WORSENING); // Apply cap

    // Ensure no negative values
    estimatedLearningLossWeeks = Math.max(0, estimatedLearningLossWeeks);
    longTermAchievementReduction = Math.max(0, longTermAchievementReduction);
    equityGapWorsening = Math.max(0, equityGapWorsening);

    return {
        estimatedLearningLossWeeks: estimatedLearningLossWeeks,
        longTermAchievementReduction: longTermAchievementReduction,
        equityGapWorsening: equityGapWorsening
    };
}
