const { numUnplannedAssignments, avgPrepTimeFactor, avgDeliveryTimeFactor, avgAssessmentTimeFactor, perStudentAssessmentModifier, avgStudentsPerClassAffected, adminCoordinationOverhead, teacherHourlyRate } = inputs;

// Handle edge cases: if numUnplannedAssignments is 0 or less, or other key inputs are non-positive, all outputs are 0.
if (numUnplannedAssignments <= 0 || avgPrepTimeFactor <= 0 || avgDeliveryTimeFactor <= 0 || avgAssessmentTimeFactor <= 0 || teacherHourlyRate <= 0) {
    return {
        totalAdditionalHours: 0,
        financialImpact: 0,
        equivalentLostPlanningDays: 0,
        qualitativeImpactScore: 0
    };
}

// Constants for base times (if complexity factor is 1.0) for a typical 'standard' unplanned task
const BASE_PREP_PER_ASSIGNMENT = 0.5; // hours (e.g., 30 mins to understand, gather resources)
const BASE_DELIVERY_PER_ASSIGNMENT = 0.25; // hours (e.g., 15 mins to explain/integrate in class)
const BASE_ASSESS_PER_ASSIGNMENT = 0.1; // hours (e.g., 6 mins for initial review/follow-up)
const STANDARD_WORK_DAY_HOURS = 7; // typical number of dedicated planning/professional development hours in a workday

// Step 1: Calculate base time for each phase per assignment, adjusted by complexity factors
const prepTimePerAssignment = BASE_PREP_PER_ASSIGNMENT * avgPrepTimeFactor;
const deliveryTimePerAssignment = BASE_DELIVERY_PER_ASSIGNMENT * avgDeliveryTimeFactor;
const baseAssessTimePerAssignment = BASE_ASSESS_PER_ASSIGNMENT * avgAssessmentTimeFactor;

// Step 2: Calculate per-student assessment time if applicable. This is added to the base assessment time.
const studentSpecificAssessTimePerAssignment = perStudentAssessmentModifier * avgStudentsPerClassAffected * avgAssessmentTimeFactor;

// Step 3: Total direct time per single unplanned assignment
const totalDirectTimePerAssignment = prepTimePerAssignment + deliveryTimePerAssignment + baseAssessTimePerAssignment + studentSpecificAssessTimePerAssignment;

// Step 4: Total direct hours for all unplanned assignments
const totalDirectHours = totalDirectTimePerAssignment * numUnplannedAssignments;

// Step 5: Apply administrative coordination overhead
const overheadFactor = Math.max(0, Math.min(100, adminCoordinationOverhead)) / 100; // Ensure overhead is between 0-100% and converted to decimal
const totalAdditionalHours = totalDirectHours * (1 + overheadFactor);

// Step 6: Calculate financial impact
const financialImpact = totalAdditionalHours * teacherHourlyRate;

// Step 7: Calculate equivalent lost professional planning days
const equivalentLostPlanningDays = totalAdditionalHours / STANDARD_WORK_DAY_HOURS;

// Step 8: Calculate qualitative impact score (0-100)
// This score aims to reflect the combined stress, disruption, and complexity.
// It's weighted: 60% from total hours, 40% from complexity factors and overhead.

// Max total hours for scoring normalization (e.g., if a teacher dedicates 40 hours to unplanned work in a month, that's significant).
// This value can be adjusted based on expected range, e.g., 40-60 hours is very high.
const MAX_REFERENCE_HOURS_FOR_SCORE = 40; 

let qualitativeImpactScore = Math.min(60, (totalAdditionalHours / MAX_REFERENCE_HOURS_FOR_SCORE) * 60);

// Add impact from average complexity factors (ranging from 1.0 to 2.0+)
const avgComplexityFactors = (avgPrepTimeFactor + avgDeliveryTimeFactor + avgAssessmentTimeFactor) / 3;
// Normalize avgComplexityFactors (e.g., 1.0 -> 0, 2.0 -> 1)
const normalizedComplexity = Math.max(0, Math.min(1, (avgComplexityFactors - 1) / 1)); // Assuming complexity factors usually range from 1 to 2
qualitativeImpactScore += normalizedComplexity * 25; // Max 25 points from complexity

// Add impact from administrative overhead (up to 15 points)
qualitativeImpactScore += overheadFactor * 15; // Max 15 points from overhead (if 100% overhead)

// Ensure score is within 0-100 range and rounded
qualitativeImpactScore = Math.round(Math.max(0, Math.min(100, qualitativeImpactScore)));

return {
    totalAdditionalHours: totalAdditionalHours,
    financialImpact: financialImpact,
    equivalentLostPlanningDays: equivalentLostPlanningDays,
    qualitativeImpactScore: qualitativeImpactScore
};
