const currentNetSalary = currentAnnualSalaryUSD * (1 - currentTaxRate / 100);
const targetAnnualSalaryUSD = targetAnnualSalaryLocal / targetCurrencyExchangeRate;
const targetNetSalary = targetAnnualSalaryUSD * (1 - targetTaxRate / 100);

const annualNetIncomeChangeUSD = targetNetSalary - currentNetSalary;
const totalUpfrontCostUSD = visaFees + movingCosts + initialSettlingCosts;

let roiPeriodMonths = 'Not Applicable'; // Default to string for clarity

if (annualNetIncomeChangeUSD > 0) {
    const calculatedRoi = (totalUpfrontCostUSD / annualNetIncomeChangeUSD) * 12;
    // Cap ROI period to a reasonable maximum, e.g., 20 years (240 months) to avoid excessively long or misleading figures
    // If upfront costs are high and income change is tiny, ROI can be very large, or if it's very small, division by zero like behavior
    roiPeriodMonths = Math.min(calculatedRoi, 240); // Cap at 20 years, convert to string if over cap or not applicable
} else if (annualNetIncomeChangeUSD < 0) {
    roiPeriodMonths = 'Never Profitable';
} else {
    roiPeriodMonths = 'No Net Income Gain';
}

return {
    totalUpfrontCostUSD: totalUpfrontCostUSD,
    annualNetIncomeChangeUSD: annualNetIncomeChangeUSD,
    roiPeriodMonths: roiPeriodMonths
};
