function calculateROI(inputs) {
    const annualRevenueImpacted = inputs.annualRevenueImpacted;
    const costOfDiversificationInitial = inputs.costOfDiversificationInitial;
    const costOfDiversificationAnnual = inputs.costOfDiversificationAnnual;
    const probabilityOfDisruption = inputs.probabilityOfDisruption;
    const impactOfDisruptionTariff = inputs.impactOfDisruptionTariff;
    const impactOfDisruptionSupplyChain = inputs.impactOfDisruptionSupplyChain;
    const brandDamageFactor = inputs.brandDamageFactor;
    const riskMitigationEffectiveness = inputs.riskMitigationEffectiveness;
    const evaluationPeriodYears = inputs.evaluationPeriodYears;

    // Input validation and clamping
    const clampedProbOfDisruption = Math.max(0, Math.min(1, probabilityOfDisruption));
    const clampedRiskMitigation = Math.max(0, Math.min(1, riskMitigationEffectiveness));
    const clampedEvaluationPeriod = Math.max(1, evaluationPeriodYears); // Minimum 1 year

    // 1. Calculate the potential annual loss if NOT diversified (Cost of Inaction)
    // This includes direct financial impacts and a factor for brand damage.
    const totalImpactFactorUndiversified = impactOfDisruptionTariff + impactOfDisruptionSupplyChain + brandDamageFactor;
    const potentialAnnualLossUndiversified = annualRevenueImpacted * totalImpactFactorUndiversified;
    const expectedAnnualLossUndiversified = potentialAnnualLossUndiversified * clampedProbOfDisruption;

    // 2. Calculate the potential annual loss IF diversified
    // Diversification reduces the impact of disruption.
    const totalImpactFactorDiversified = totalImpactFactorUndiversified * (1 - clampedRiskMitigation);
    const potentialAnnualLossDiversified = annualRevenueImpacted * totalImpactFactorDiversified;
    const expectedAnnualLossDiversified = potentialAnnualLossDiversified * clampedProbOfDisruption;

    // 3. Calculate annual risk savings from diversification
    const annualRiskSavings = expectedAnnualLossUndiversified - expectedAnnualLossDiversified;

    // 4. Calculate total costs of diversification over the evaluation period
    const totalCostOfDiversification = costOfDiversificationInitial + (costOfDiversificationAnnual * clampedEvaluationPeriod);

    // 5. Calculate total benefits (risk savings) over the evaluation period
    const totalRiskSavingsOverPeriod = annualRiskSavings * clampedEvaluationPeriod;

    // 6. Calculate Net Benefit and ROI
    const netBenefit = totalRiskSavingsOverPeriod - totalCostOfDiversification;

    let returnOnInvestment = 0;
    if (totalCostOfDiversification > 0) {
        returnOnInvestment = (netBenefit / totalCostOfDiversification) * 100;
    } else if (netBenefit > 0) {
        returnOnInvestment = Infinity; // Infinite ROI if cost is zero and benefit is positive
    } else {
        returnOnInvestment = 0; // If cost is zero and no benefit or negative benefit
    }

    return {
        totalCostOfDiversification: totalCostOfDiversification,
        totalRiskSavings: totalRiskSavingsOverPeriod,
        netBenefit: netBenefit,
        returnOnInvestment: returnOnInvestment
    };
}
