const WALL_MATERIAL_COSTS = {
    'Tarpaulin': 1.2, 
    'Plywood (12mm)': 5.5, 
    'Corrugated Metal': 11.0 
};
const ROOF_MATERIAL_COSTS = {
    'Heavy-duty Tarpaulin': 2.5, 
    'Corrugated Metal Sheet': 12.0 
};
const FRAMING_MATERIAL_COSTS = {
    'Timber (2x4 equivalent)': 3.2, 
    'Bamboo (Large)': 1.8 
};
const ROOF_OVERHANG_FACTOR = 1.05; 
const FRAMING_SPAN_FOR_RAFTERS = 1.0; 
const FRAMING_POST_SPAN = 2.0; 
const length = inputs.shelterLength;
const width = inputs.shelterWidth;
const wallHeight = inputs.wallHeight;
let roofPitchDegrees = inputs.roofPitchDegrees; 
const wallMaterialType = inputs.wallMaterialType;
const roofMaterialType = inputs.roofMaterialType;
const framingMaterialType = inputs.framingMaterialType;
const wasteFactor = inputs.wasteFactor;

if (length < 1) return { error: "Shelter length must be at least 1 meter." };
if (width < 1) return { error: "Shelter width must be at least 1 meter." };
if (wallHeight < 1.5) return { error: "Wall height must be at least 1.5 meters." };
if (wallHeight > 3) return { error: "Wall height cannot exceed 3 meters." };

roofPitchDegrees = Math.max(0, Math.min(roofPitchDegrees, 45)); 

const wasteMultiplier = 1 + (wasteFactor / 100);

let calculatedWallArea = (2 * (length + width) * wallHeight);

const roofPitchRadians = roofPitchDegrees * Math.PI / 180;
const slopedWidth = width / Math.cos(roofPitchRadians);
let calculatedRoofArea = length * slopedWidth * ROOF_OVERHANG_FACTOR;

let totalFramingLength = 0;

const numCornerPosts = 4;
totalFramingLength += numCornerPosts * wallHeight;

const numIntermediatePostsLength = Math.max(0, Math.ceil(length / FRAMING_POST_SPAN) - 1) * 2; 
const numIntermediatePostsWidth = Math.max(0, Math.ceil(width / FRAMING_POST_SPAN) - 1) * 2; 
totalFramingLength += (numIntermediatePostsLength + numIntermediatePostsWidth) * wallHeight;

const topAndBottomPlatesLength = 2 * (length + width); 
totalFramingLength += topAndBottomPlatesLength;

const numRafters = Math.ceil(length / FRAMING_SPAN_FOR_RAFTERS);
const raftersLength = numRafters * slopedWidth;
totalFramingLength += raftersLength;

let framingLinearMeters = totalFramingLength;

calculatedWallArea *= wasteMultiplier;
calculatedRoofArea *= wasteMultiplier;
framingLinearMeters *= wasteMultiplier;

const estimatedWallMaterialCost = calculatedWallArea * WALL_MATERIAL_COSTS[wallMaterialType];
const estimatedRoofMaterialCost = calculatedRoofArea * ROOF_MATERIAL_COSTS[roofMaterialType];
const estimatedFramingCost = framingLinearMeters * FRAMING_MATERIAL_COSTS[framingMaterialType];

const totalEstimatedMaterialCost = estimatedWallMaterialCost + estimatedRoofMaterialCost + estimatedFramingCost;

return {
    calculatedWallArea,
    calculatedRoofArea,
    framingLinearMeters,
    estimatedWallMaterialCost,
    estimatedRoofMaterialCost,
    estimatedFramingCost,
    totalEstimatedMaterialCost
};
