function calculate({ numberOfResidentialUnits, averageMonthlyRentPerUnit, totalCurrentAnnualOperatingExpenses, marketRentGrowthRateAnnual, policyRentControlCapAnnual, policyRelatedCostIncreaseAnnualPercentage, relocationAssistanceCostPerIncident, annualPolicyTriggerIncidents, propertyValueDiscountRate, longTermHorizonYears }) {

  // Input validation and default values
  numberOfResidentialUnits = Math.max(1, parseFloat(numberOfResidentialUnits) || 1);
  averageMonthlyRentPerUnit = Math.max(0, parseFloat(averageMonthlyRentPerUnit) || 0);
  totalCurrentAnnualOperatingExpenses = Math.max(0, parseFloat(totalCurrentAnnualOperatingExpenses) || 0);
  marketRentGrowthRateAnnual = parseFloat(marketRentGrowthRateAnnual) / 100 || 0;
  policyRentControlCapAnnual = parseFloat(policyRentControlCapAnnual) / 100 || 0;
  policyRelatedCostIncreaseAnnualPercentage = parseFloat(policyRelatedCostIncreaseAnnualPercentage) / 100 || 0;
  relocationAssistanceCostPerIncident = Math.max(0, parseFloat(relocationAssistanceCostPerIncident) || 0);
  annualPolicyTriggerIncidents = Math.max(0, parseFloat(annualPolicyTriggerIncidents) || 0);
  propertyValueDiscountRate = parseFloat(propertyValueDiscountRate) / 100 || 0.08; // Default to 8%
  longTermHorizonYears = Math.max(1, parseInt(longTermHorizonYears) || 10);

  // Step 1: Calculate Estimated Annual Revenue Loss from Rent Control
  const currentAnnualRevenuePerUnit = averageMonthlyRentPerUnit * 12;
  const potentialMarketRentIncreasePerUnit = currentAnnualRevenuePerUnit * marketRentGrowthRateAnnual;
  const allowedPolicyRentIncreasePerUnit = currentAnnualRevenuePerUnit * policyRentControlCapAnnual;

  let annualRevenueLossPerUnit = 0;
  if (policyRentControlCapAnnual < marketRentGrowthRateAnnual) {
    annualRevenueLossPerUnit = potentialMarketRentIncreasePerUnit - allowedPolicyRentIncreasePerUnit;
  }
  // If market growth is less than or equal to policy cap, no direct revenue loss from the cap itself, assuming market is the ceiling.
  // This model assumes the 'loss' is the delta between market potential and policy restriction.

  const estimatedAnnualRevenueLoss = annualRevenueLossPerUnit * numberOfResidentialUnits;

  // Step 2: Calculate Estimated Annual Operating Cost Increase
  const additionalOperatingExpensesFromPolicy = totalCurrentAnnualOperatingExpenses * policyRelatedCostIncreaseAnnualPercentage;
  const totalRelocationAssistanceCostsAnnual = relocationAssistanceCostPerIncident * annualPolicyTriggerIncidents;

  const estimatedAnnualOperatingCostIncrease = additionalOperatingExpensesFromPolicy + totalRelocationAssistanceCostsAnnual;

  // Step 3: Calculate Net Operating Income (NOI) Impact Annually
  const netOperatingIncomeImpactAnnual = - (estimatedAnnualRevenueLoss + estimatedAnnualOperatingCostIncrease);

  // Step 4: Calculate Long-Term Property Value Impact (NPV)
  let longTermPropertyValueImpact = 0;
  if (propertyValueDiscountRate > -1) { // Prevent division by zero or negative discount rates causing infinite values
    for (let i = 1; i <= longTermHorizonYears; i++) {
      longTermPropertyValueImpact += netOperatingIncomeImpactAnnual / Math.pow(1 + propertyValueDiscountRate, i);
    }
  }

  // Step 5: Calculate Total Policy Compliance Cost Per Unit Annually
  const totalPolicyComplianceCostPerUnitAnnual = (estimatedAnnualRevenueLoss + estimatedAnnualOperatingCostIncrease) / numberOfResidentialUnits;

  return {
    estimatedAnnualRevenueLoss: estimatedAnnualRevenueLoss,
    estimatedAnnualOperatingCostIncrease: estimatedAnnualOperatingCostIncrease,
    netOperatingIncomeImpactAnnual: netOperatingIncomeImpactAnnual,
    longTermPropertyValueImpact: longTermPropertyValueImpact,
    totalPolicyComplianceCostPerUnitAnnual: totalPolicyComplianceCostPerUnitAnnual
  };
}
