var room_area = inputs.room_length * inputs.room_width;
var tile_length_ft_with_grout = (inputs.tile_length + inputs.grout_width) / 12;
var tile_width_ft_with_grout = (inputs.tile_width + inputs.grout_width) / 12;
var tiles_per_length = Math.ceil(inputs.room_length / tile_length_ft_with_grout);
var tiles_per_width = Math.ceil(inputs.room_width / tile_width_ft_with_grout);
var total_base_tiles = tiles_per_length * tiles_per_width;
var final_tiles = Math.ceil(total_base_tiles * (1 + inputs.waste_percentage / 100));

return {
  total_tiles: final_tiles,
  calculated_area: room_area
};
