function calculateCosts(inputs) {
    const {
        annualRevenue,
        numHighRiskCountries,
        avgMonthlyTransactionVolume,
        avgTransactionValue,
        complianceTeamSize,
        avgComplianceSalaryAnnual,
        annualTechSubscriptionCost,
        externalLegalHoursAnnual,
        avgLegalHourlyRate,
        potentialRevenueImpactPercent
    } = inputs;

    // Sanitize inputs to ensure non-negative values and apply caps
    const sanitizedAnnualRevenue = Math.max(0, annualRevenue);
    const sanitizedNumHighRiskCountries = Math.max(0, Math.min(10, numHighRiskCountries));
    const sanitizedAvgMonthlyTransactionVolume = Math.max(0, avgMonthlyTransactionVolume);
    const sanitizedAvgTransactionValue = Math.max(0, avgTransactionValue);
    const sanitizedComplianceTeamSize = Math.max(0, complianceTeamSize);
    const sanitizedAvgComplianceSalaryAnnual = Math.max(0, avgComplianceSalaryAnnual);
    const sanitizedAnnualTechSubscriptionCost = Math.max(0, annualTechSubscriptionCost);
    const sanitizedExternalLegalHoursAnnual = Math.max(0, externalLegalHoursAnnual);
    const sanitizedAvgLegalHourlyRate = Math.max(0, avgLegalHourlyRate);
    const sanitizedPotentialRevenueImpactPercent = Math.max(0, Math.min(100, potentialRevenueImpactPercent));

    // 1. Personnel Cost
    let personnelCost = sanitizedComplianceTeamSize * sanitizedAvgComplianceSalaryAnnual;
    if (sanitizedComplianceTeamSize > 0 && personnelCost === 0) {
        // Apply a floor if a team member is indicated but salary is zero
        personnelCost = 5000; 
    } else if (sanitizedComplianceTeamSize === 0) {
        personnelCost = 0;
    }

    // 2. Technology Cost
    let technologyCost = sanitizedAnnualTechSubscriptionCost;
    if (sanitizedNumHighRiskCountries > 0 && technologyCost < 5000) {
        // Minimum tech cost if high risk countries are involved, suggesting a basic screening tool is essential
        technologyCost = Math.max(technologyCost, 5000); 
    }

    // 3. Legal Cost
    let legalCost = sanitizedExternalLegalHoursAnnual * sanitizedAvgLegalHourlyRate;
    if (sanitizedNumHighRiskCountries > 0 && legalCost < 2500) {
        // Minimum legal cost for high-risk engagement, acknowledging need for occasional counsel
        legalCost = Math.max(legalCost, 2500); 
    }

    // 4. Due Diligence Cost Calculation
    // Base cost per transaction for automated screening and basic checks
    const baseTransactionScreeningCost = sanitizedAvgMonthlyTransactionVolume * 12 * 1.5; // $1.5 per transaction

    // Complexity factor based on annual revenue: larger companies have more complex structures and need deeper DD
    // Logarithmic scale helps manage the impact for very large revenues, preventing unrealistic escalation
    const revenueComplexityFactor = Math.log10(sanitizedAnnualRevenue / 1000000 + 1) * 7500; 

    // Risk multiplier based on number of high-risk countries: higher risk means more intensive scrutiny
    const riskMultiplier = 1 + (sanitizedNumHighRiskCountries * 0.20); // 20% increase per high-risk country

    // Additional complexity for high-value transactions: these often require enhanced due diligence
    const transactionValueComplexity = (sanitizedAvgTransactionValue > 50000 ? (sanitizedAvgTransactionValue / 50000) * 100 : 0);

    let dueDiligenceCost = (baseTransactionScreeningCost + revenueComplexityFactor + transactionValueComplexity) * riskMultiplier;

    // Ensure a reasonable minimum for due diligence if there's any activity or risk, otherwise 0
    if (sanitizedAvgMonthlyTransactionVolume > 0 || sanitizedNumHighRiskCountries > 0) {
        dueDiligenceCost = Math.max(dueDiligenceCost, 3000); 
    } else {
        dueDiligenceCost = 0;
    }

    // 5. Market Opportunity Cost
    let marketOpportunityCost = sanitizedAnnualRevenue * (sanitizedPotentialRevenueImpactPercent / 100);
    // Cap market opportunity cost to be no more than the annual revenue
    marketOpportunityCost = Math.min(marketOpportunityCost, sanitizedAnnualRevenue);

    // Total Compliance Cost
    const totalComplianceCost = personnelCost + technologyCost + legalCost + dueDiligenceCost + marketOpportunityCost;

    return {
        personnelCost: parseFloat(personnelCost.toFixed(2)),
        technologyCost: parseFloat(technologyCost.toFixed(2)),
        legalCost: parseFloat(legalCost.toFixed(2)),
        dueDiligenceCost: parseFloat(dueDiligenceCost.toFixed(2)),
        marketOpportunityCost: parseFloat(marketOpportunityCost.toFixed(2)),
        totalComplianceCost: parseFloat(totalComplianceCost.toFixed(2)),
    };
}
