const distance_km = inputs.distance_km || 0;
const passengers = Math.max(1, inputs.passengers || 1);

let car_distance_percentage = inputs.car_distance_percentage || 0;
let plane_distance_percentage = inputs.plane_distance_percentage || 0;
let train_distance_percentage = inputs.train_distance_percentage || 0;

// Cap percentages at 100 and ensure non-negative
car_distance_percentage = Math.max(0, Math.min(100, car_distance_percentage));
plane_distance_percentage = Math.max(0, Math.min(100, plane_distance_percentage));
train_distance_percentage = Math.max(0, Math.min(100, train_distance_percentage));

// Normalize percentages if they don't sum to 100, or if sum is 0, make all 0
const total_input_percentage = car_distance_percentage + plane_distance_percentage + train_distance_percentage;
let norm_car_pct = 0, norm_plane_pct = 0, norm_train_pct = 0;

if (total_input_percentage > 0) {
    norm_car_pct = car_distance_percentage / total_input_percentage;
    norm_plane_pct = plane_distance_percentage / total_input_percentage;
    norm_train_pct = train_distance_percentage / total_input_percentage;
} else if (distance_km > 0) { // If distance is >0 but percentages sum to 0, distribute evenly as a fallback
    norm_car_pct = 1/3; 
    norm_plane_pct = 1/3;
    norm_train_pct = 1/3;
}

const car_distance = distance_km * norm_car_pct;
const plane_distance = distance_km * norm_plane_pct;
const train_distance = distance_km * norm_train_pct;

// CO2 emission factors (g CO2 / unit)
// Car: g CO2 / km (per vehicle). Average for a modern petrol car is ~120-150.
// Plane: g CO2 / passenger-km (for economy class, short-medium haul). ~180.
// Train: g CO2 / passenger-km (for electric, average EU grid). ~40.

// Car calculations
const default_car_co2_g_per_km = 120; // g CO2 per km for the vehicle
const car_co2_g_per_km_vehicle = inputs.car_custom_co2_g_km > 0 ? inputs.car_custom_co2_g_km : default_car_co2_g_per_km;
const car_total_co2_g = car_co2_g_per_km_vehicle * car_distance; // Total vehicle emissions for car leg
const car_co2_kg = car_total_co2_g / 1000;

// Plane calculations
const default_plane_co2_g_per_pkm_economy = 180; // g CO2 per passenger-km
const plane_class_multiplier = Math.max(1, inputs.plane_class_factor || 1); // Economy: 1, Business: ~1.5, First: ~2.5
const plane_co2_g_per_pkm = default_plane_co2_g_per_pkm_economy * plane_class_multiplier;
const plane_total_co2_g = plane_co2_g_per_pkm * plane_distance * passengers; // Total emissions for plane leg
const plane_co2_kg = plane_total_co2_g / 1000;

// Train calculations
const default_train_co2_g_per_pkm = 40; // g CO2 per passenger-km
const train_total_co2_g = default_train_co2_g_per_pkm * train_distance * passengers; // Total emissions for train leg
const train_co2_kg = train_total_co2_g / 1000;

// Total calculations
const total_co2_kg = car_co2_kg + plane_co2_kg + train_co2_kg;
const co2_per_passenger_kg = total_co2_kg / passengers; // Total emissions divided among all passengers

return {
    total_co2_kg: total_co2_kg,
    co2_per_passenger_kg: co2_per_passenger_kg,
    car_co2_kg: car_co2_kg,
    plane_co2_kg: plane_co2_kg,
    train_co2_kg: train_co2_kg
};
