function calculate() {

    const inputs = {
        travelMode: travelMode,
        distanceKm: parseFloat(distanceKm),
        numTravelers: parseFloat(numTravelers),
        carFuelTypeCurrent: carFuelTypeCurrent,
        carEfficiencyCurrent: parseFloat(carEfficiencyCurrent),
        fuelCostCurrentPerUnit: parseFloat(fuelCostCurrentPerUnit),
        alternativeOption: alternativeOption,
        carFuelTypeAlternative: carFuelTypeAlternative,
        carEfficiencyAlternative: parseFloat(carEfficiencyAlternative),
        fuelCostAlternativePerUnit: parseFloat(fuelCostAlternativePerUnit)
    };

    // Default values for non-car modes or missing inputs
    const DEFAULT_PASSENGER_KM_EMISSIONS = {
        'Plane': 0.12, // kg CO2e/pkm (short-medium haul, ICAO/EEA average)
        'Train': 0.015, // kg CO2e/pkm (electric, average EU/IEA)
        'Bus': 0.08 // kg CO2e/pkm (average urban/intercity, IEA/EEA)
    };

    const FUEL_EMISSION_FACTORS = {
        'Gasoline': 2.31, // kg CO2e/L (EPA/IEA average)
        'Diesel': 2.68, // kg CO2e/L (EPA/IEA average)
        'Bioethanol': 1.2, // kg CO2e/L (approx. 50% of gasoline, but varies significantly by source)
        'Electric': 0.35 // kg CO2e/kWh (EU-27 average grid mix, varies greatly by region)
    };

    // Helper function to calculate emissions and cost for a given mode
    function getModeEmissionsAndCost(mode, distance, travelers, fuelType, efficiency, fuelCostPerUnit) {
        if (isNaN(distance) || distance <= 0 || isNaN(travelers) || travelers <= 0) {
            return { emissions: 0, cost: 0 };
        }

        let totalEmissions = 0;
        let totalCost = 0;

        switch (mode) {
            case 'Car':
                if (!fuelType || isNaN(efficiency) || efficiency <= 0) {
                    return { emissions: 0, cost: 0 }; // Insufficient valid data for car
                }
                const fuelNeededPer100km = efficiency; // L/100km or kWh/100km
                const totalFuelNeeded = (distance / 100) * fuelNeededPer100km;

                if (fuelType === 'Electric') {
                    totalEmissions = totalFuelNeeded * FUEL_EMISSION_FACTORS.Electric;
                    totalCost = totalFuelNeeded * (isNaN(fuelCostPerUnit) ? 0 : fuelCostPerUnit);
                } else {
                    totalEmissions = totalFuelNeeded * FUEL_EMISSION_FACTORS[fuelType];
                    totalCost = totalFuelNeeded * (isNaN(fuelCostPerUnit) ? 0 : fuelCostPerUnit);
                }
                break;
            case 'Plane':
            case 'Train':
            case 'Bus':
                const emissionsPerPkm = DEFAULT_PASSENGER_KM_EMISSIONS[mode];
                totalEmissions = emissionsPerPkm * distance * travelers;
                totalCost = 0; // Financial savings for these modes are not calculated dynamically here, only for fuel-based options
                break;
            case 'Walk/Bike':
                totalEmissions = 0;
                totalCost = 0;
                break;
            default:
                return { emissions: 0, cost: 0 };
        }
        return { emissions: totalEmissions, cost: totalCost };
    }

    // Current Scenario Calculation
    const currentCarFuelType = inputs.travelMode === 'Car' ? inputs.carFuelTypeCurrent : null;
    const currentCarEfficiency = inputs.travelMode === 'Car' ? inputs.carEfficiencyCurrent : null;
    const currentFuelCost = inputs.travelMode === 'Car' ? inputs.fuelCostCurrentPerUnit : null;

    const currentResult = getModeEmissionsAndCost(
        inputs.travelMode,
        inputs.distanceKm,
        inputs.numTravelers,
        currentCarFuelType,
        currentCarEfficiency,
        currentFuelCost
    );

    // Alternative Scenario Calculation
    let alternativeResult = { emissions: 0, cost: 0 };
    if (inputs.alternativeOption && inputs.alternativeOption !== 'None') {
        let altMode = inputs.alternativeOption;
        let altFuelType = null;
        let altEfficiency = null;
        let altFuelCost = null;

        if (altMode === 'More Efficient Car' || altMode === 'Electric Car') {
            altMode = 'Car'; // Treat as car for calculation logic
            altFuelType = inputs.carFuelTypeAlternative;
            altEfficiency = inputs.carEfficiencyAlternative;
            altFuelCost = inputs.fuelCostAlternativePerUnit;
        } else if (altMode === 'Walk/Bike') {
            // Handled by getModeEmissionsAndCost returning 0,0
        }
        // For Plane, Train, Bus, altFuelType, altEfficiency, altFuelCost will remain null/undefined

        alternativeResult = getModeEmissionsAndCost(
            altMode,
            inputs.distanceKm,
            inputs.numTravelers,
            altFuelType,
            altEfficiency,
            altFuelCost
        );
    }

    // Outputs
    const totalCarbonEmissionsKg = currentResult.emissions;
    const carbonEmissionsPerTravelerKg = inputs.numTravelers > 0 ? currentResult.emissions / inputs.numTravelers : 0;
    const totalFuelCostCurrent = currentResult.cost;

    const totalCarbonEmissionsAlternativeKg = alternativeResult.emissions;
    const carbonEmissionsSavingsKg = totalCarbonEmissionsKg - totalCarbonEmissionsAlternativeKg;
    const totalFuelCostAlternative = alternativeResult.cost;
    const financialSavings = totalFuelCostCurrent - totalFuelCostAlternative;

    const emissionReductionPercentage = totalCarbonEmissionsKg > 0 
        ? (carbonEmissionsSavingsKg / totalCarbonEmissionsKg) * 100 
        : 0;
    const costReductionPercentage = totalFuelCostCurrent > 0 
        ? (financialSavings / totalFuelCostCurrent) * 100 
        : 0;

    return {
        totalCarbonEmissionsKg,
        carbonEmissionsPerTravelerKg,
        totalFuelCostCurrent,
        totalCarbonEmissionsAlternativeKg,
        carbonEmissionsSavingsKg,
        totalFuelCostAlternative,
        financialSavings,
        emissionReductionPercentage,
        costReductionPercentage
    };
}
