const carbonFactors = {
    "economy_flight": 0.113, // Average short/medium haul kg CO2e/km/passenger
    "train": 0.014,        // Average kg CO2e/km/passenger
    "bus": 0.028,          // Average kg CO2e/km/passenger
    "car": 0.150           // Average solo driver kg CO2e/km/passenger
};

// Validate inputs and set defaults if necessary
const originalFlightDistanceKm = parseFloat(inputs.original_flight_distance_km) || 0;
const originalTravelMode = inputs.original_travel_mode;
const disruptionDurationHours = parseFloat(inputs.disruption_duration_hours) || 0;
const numTravelers = parseFloat(inputs.num_travelers) || 1;
const alternativeTransportCost = parseFloat(inputs.alternative_transport_cost) || 0;
const alternativeTransportCo2eKg = parseFloat(inputs.alternative_transport_co2e_kg) || 0;
const accommodationCost = parseFloat(inputs.accommodation_cost) || 0;
const mealsIncidentalsCost = parseFloat(inputs.meals_incidentals_cost) || 0;
const lostIncomePerHour = parseFloat(inputs.lost_income_per_hour) || 0;

// Ensure non-negative and valid values
const clamp = (value, min) => Math.max(value, min);

const validatedOriginalFlightDistanceKm = clamp(originalFlightDistanceKm, 0);
const validatedDisruptionDurationHours = clamp(disruptionDurationHours, 0);
const validatedNumTravelers = clamp(numTravelers, 1);
const validatedAlternativeTransportCost = clamp(alternativeTransportCost, 0);
const validatedAlternativeTransportCo2eKg = clamp(alternativeTransportCo2eKg, 0);
const validatedAccommodationCost = clamp(accommodationCost, 0);
const validatedMealsIncidentalsCost = clamp(mealsIncidentalsCost, 0);
const validatedLostIncomePerHour = clamp(lostIncomePerHour, 0);


// 1. Calculate Original Planned Carbon Footprint
let originalCarbonFootprint = 0;
const originalFactor = carbonFactors[originalTravelMode];
if (originalFactor) {
    originalCarbonFootprint = validatedOriginalFlightDistanceKm * originalFactor * validatedNumTravelers;
} else {
    // If mode not recognized, assume 0 or handle as an error if strict validation is needed
    originalCarbonFootprint = 0; 
}

// 2. Calculate Cost of Lost Time
const lostTimeCost = validatedDisruptionDurationHours * validatedNumTravelers * validatedLostIncomePerHour;

// 3. Calculate Direct Disruption Expenses (excluding lost time)
const directDisruptionExpenses = validatedAlternativeTransportCost + validatedAccommodationCost + validatedMealsIncidentalsCost;

// 4. Calculate Total Financial Cost of Disruption
const totalFinancialCostDisruption = directDisruptionExpenses + lostTimeCost;

// 5. Total Carbon Footprint Due to Disruption-Related Travel
const totalCarbonFootprintDisruption = validatedAlternativeTransportCo2eKg;

// 6. Calculate Net Change in CO2e Footprint
const netCarbonImpact = totalCarbonFootprintDisruption - originalCarbonFootprint;

return {
    total_financial_cost_disruption: totalFinancialCostDisruption,
    total_carbon_footprint_disruption: totalCarbonFootprintDisruption,
    original_carbon_footprint: originalCarbonFootprint,
    net_carbon_impact: netCarbonImpact,
    lost_time_cost: lostTimeCost,
    direct_disruption_expenses: directDisruptionExpenses
};
