function calculate({ initialEndowment, investmentReturnRate, spendingRate, annualContributions = 0, contributionGrowthRate = 0, forecastYears }) {
    let currentEndowment = initialEndowment;
    let totalSpending = 0;
    let totalContributions = 0;

    const invRate = investmentReturnRate / 100;
    const spdRate = spendingRate / 100;
    const contribGrowth = contributionGrowthRate / 100;

    if (forecastYears <= 0) {
        return {
            finalEndowmentValue: initialEndowment,
            totalSpendingOverPeriod: 0,
            totalContributionsOverPeriod: 0
        };
    }

    for (let year = 1; year <= forecastYears; year++) {
        const annualInvestmentGain = currentEndowment * invRate;
        const annualSpending = currentEndowment * spdRate;

        let currentYearContributions = annualContributions;
        if (annualContributions > 0 && year > 1) {
            currentYearContributions = annualContributions * Math.pow(1 + contribGrowth, year - 1);
        }

        currentEndowment = currentEndowment + annualInvestmentGain - annualSpending + currentYearContributions;

        totalSpending += annualSpending;
        totalContributions += currentYearContributions;
    }

    return {
        finalEndowmentValue: currentEndowment,
        totalSpendingOverPeriod: totalSpending,
        totalContributionsOverPeriod: totalContributions
    };
}
