function calculateROI(inputs) {
    const upfrontCost = inputs.upfrontCost;
    const annualEnergySavingsKWh = inputs.annualEnergySavingsKWh;
    const currentElectricityPrice = inputs.currentElectricityPrice;
    const priceIncreaseRate = inputs.priceIncreaseRate / 100;
    const annualMaintenanceSavings = inputs.annualMaintenanceSavings;
    const projectLifespanYears = inputs.projectLifespanYears;
    const discountRate = inputs.discountRate / 100;

    if (upfrontCost <= 0 || projectLifespanYears <= 0) {
        return {
            firstYearSavings: 0,
            simplePaybackYears: Infinity,
            roiPercentage: -100,
            npv: upfrontCost === 0 ? 0 : -upfrontCost,
            discountedPaybackYears: Infinity
        };
    }

    let firstYearEnergySavings = annualEnergySavingsKWh * currentElectricityPrice;
    let firstYearTotalSavings = firstYearEnergySavings + annualMaintenanceSavings;

    let simplePaybackYears = Infinity;
    if (firstYearTotalSavings > 0) {
        simplePaybackYears = upfrontCost / firstYearTotalSavings;
    }

    let cumulativeDiscountedCashFlow = -upfrontCost;
    let discountedPaybackYears = Infinity;
    let totalUndiscountedSavings = 0;

    for (let year = 1; year <= projectLifespanYears; year++) {
        let annualEnergySavingsThisYear = annualEnergySavingsKWh * currentElectricityPrice * Math.pow(1 + priceIncreaseRate, year - 1);
        let totalAnnualSavingsThisYear = annualEnergySavingsThisYear + annualMaintenanceSavings;
        totalUndiscountedSavings += totalAnnualSavingsThisYear;

        const discountFactor = Math.pow(1 + discountRate, year);
        const discountedAnnualSavings = totalAnnualSavingsThisYear / discountFactor;

        cumulativeDiscountedCashFlow += discountedAnnualSavings;

        if (discountedPaybackYears === Infinity && cumulativeDiscountedCashFlow >= 0) {
            const previousCumulativeDCF = cumulativeDiscountedCashFlow - discountedAnnualSavings;
            discountedPaybackYears = year - 1 + (-previousCumulativeDCF / discountedAnnualSavings);
        }
    }

    const roiPercentage = ((totalUndiscountedSavings - upfrontCost) / upfrontCost) * 100;

    return {
        firstYearSavings: firstYearTotalSavings,
        simplePaybackYears: simplePaybackYears,
        roiPercentage: roiPercentage,
        npv: cumulativeDiscountedCashFlow,
        discountedPaybackYears: discountedPaybackYears
    };
}
