function calculate({ boardCompositionShift, stateFundingPriorityChange, tuitionPolicyAutonomy, endowmentUtilizationFocus, researchAcademicBalance, studentAidPolicyShift }) {

  // Constants and Assumptions (simplified for demonstration)
  const BASE_ANNUAL_BUDGET_BILLIONS = 1.0; // Assume a baseline annual budget of $1 Billion for proportionality
  const BASE_TUITION_REVENUE_PERCENT = 0.35; // 35% of budget from tuition
  const BASE_STATE_APPROP_PERCENT = 0.25; // 25% of budget from state appropriation
  const BASE_ENDOWMENT_CONTRIBUTION_PERCENT = 0.03; // 3% of budget from endowment draw

  // Impact weights for each input on various outputs
  const WEIGHTS = {
    boardCompositionShift: {
      funding: -0.005, // Moderate negative impact on funding autonomy if state-controlled, positive for traditional
      academic: 0.15, // State-focus might lead to more vocational programs, traditional to more research
      student: -0.1, // State-focus might prioritize in-state or certain demographics, traditional broader access
      research: -0.15 // State-focus might deprioritize basic research
    },
    stateFundingPriorityChange: {
      funding: 0.02, // Direct impact on funding
      academic: 0.2, // Positive impact on program investment
      student: 0.3, // Allows for more aid/support
      research: 0.15 // Enables research investment
    },
    tuitionPolicyAutonomy: {
      funding: 0.007, // Higher autonomy allows for more tuition revenue, but balanced
      academic: 0.1, // More revenue means more program flexibility
      student: -0.008, // Higher tuition autonomy could negatively impact access if not balanced with aid
      research: 0.05 // Revenue can support research infrastructure
    },
    endowmentUtilizationFocus: {
      funding: 0.05, // Direct impact (positive for operational, negative for strategic long-term growth)
      academic: -0.1, // More operational use might mean less for strategic program development
      student: -0.1, // Less for scholarships/student support funds
      research: -0.15 // Less for research chairs/seed grants
    },
    researchAcademicBalance: {
      funding: 0.001, // Indirect positive for research grants, but can shift internal allocations
      academic: 0.2, // Stronger research focus often leads to specialized programs
      student: -0.05, // Could shift focus from undergraduate teaching to grad research
      research: 0.5 // Direct positive impact
    },
    studentAidPolicyShift: {
      funding: -0.002, // More aid means less direct tuition revenue
      academic: 0.05, // Better student body can indirectly enhance academic environment
      student: 0.4, // Direct positive impact on access and success
      research: 0.01 // Indirect, as better students attract better faculty
    }
  };

  // 1. Calculate Projected Annual Funding Impact
  let stateFundingChangeValue = BASE_ANNUAL_BUDGET_BILLIONS * BASE_STATE_APPROP_PERCENT * (stateFundingPriorityChange / 100); // in billions
  let tuitionRevenueChangeValue = BASE_ANNUAL_BUDGET_BILLIONS * BASE_TUITION_REVENUE_PERCENT * ((tuitionPolicyAutonomy - 50) / 100 * 0.1); // Simplified: +/-10% max revenue change based on autonomy, baseline 50 index is neutral.
  let endowmentChangeValue = BASE_ANNUAL_BUDGET_BILLIONS * BASE_ENDOWMENT_CONTRIBUTION_PERCENT * (endowmentUtilizationFocus / 100 * 5); // Max +/-5% change in endowment contribution

  let boardCompositionFundingEffect = boardCompositionShift * WEIGHTS.boardCompositionShift.funding * BASE_ANNUAL_BUDGET_BILLIONS; // Additional effect
  let studentAidFundingEffect = studentAidPolicyShift * WEIGHTS.studentAidPolicyShift.funding * BASE_ANNUAL_BUDGET_BILLIONS;

  let projectedAnnualFundingImpact = (stateFundingChangeValue + tuitionRevenueChangeValue + endowmentChangeValue + boardCompositionFundingEffect + studentAidFundingEffect) * 1000; // Convert to millions USD

  // Cap funding impact to prevent extreme values
  projectedAnnualFundingImpact = Math.max(-500, Math.min(500, projectedAnnualFundingImpact)); // Max +/- $500M impact

  // 2. Calculate Academic Program Innovation Index (Base 50)
  let academicProgramInnovationIndex = 50;
  academicProgramInnovationIndex += boardCompositionShift * WEIGHTS.boardCompositionShift.academic; // e.g., State-focus might push for more vocational/aligned programs
  academicProgramInnovationIndex += stateFundingPriorityChange * WEIGHTS.stateFundingPriorityChange.academic; // Increased funding can support new programs
  academicProgramInnovationIndex += (tuitionPolicyAutonomy - 50) * WEIGHTS.tuitionPolicyAutonomy.academic; // Revenue flexibility can aid innovation
  academicProgramInnovationIndex += endowmentUtilizationFocus * WEIGHTS.endowmentUtilizationFocus.academic; // Less for strategic, less innovation
  academicProgramInnovationIndex += (researchAcademicBalance - 50) * WEIGHTS.researchAcademicBalance.academic; // Research focus often drives innovation
  academicProgramInnovationIndex += studentAidPolicyShift * WEIGHTS.studentAidPolicyShift.academic; // Indirect, better students can elevate programs

  academicProgramInnovationIndex += projectedAnnualFundingImpact / 1000 * 10; // Positive funding impact boosts innovation (scaled)

  academicProgramInnovationIndex = Math.max(0, Math.min(100, academicProgramInnovationIndex)); // Cap between 0 and 100

  // 3. Calculate Student Access & Success Index (Base 50)
  let studentAccessAndSuccessIndex = 50;
  studentAccessAndSuccessIndex += boardCompositionShift * WEIGHTS.boardCompositionShift.student;
  studentAccessAndSuccessIndex += stateFundingPriorityChange * WEIGHTS.stateFundingPriorityChange.student;
  studentAccessAndSuccessIndex += (tuitionPolicyAutonomy - 50) * WEIGHTS.tuitionPolicyAutonomy.student; // Higher autonomy can mean higher tuition, impacting access
  studentAccessAndSuccessIndex += endowmentUtilizationFocus * WEIGHTS.endowmentUtilizationFocus.student; // Less for aid
  studentAccessAndSuccessIndex += (researchAcademicBalance - 50) * WEIGHTS.researchAcademicBalance.student; // Too much research focus can detract from undergraduate support
  studentAccessAndSuccessIndex += studentAidPolicyShift * WEIGHTS.studentAidPolicyShift.student; // Direct positive for need-based aid

  studentAccessAndSuccessIndex += projectedAnnualFundingImpact / 1000 * 5; // Positive funding impact can improve support services and aid.

  studentAccessAndSuccessIndex = Math.max(0, Math.min(100, studentAccessAndSuccessIndex));

  // 4. Calculate Research & Scholarly Prestige Metric (Base 50)
  let researchPrestigeMetric = 50;
  researchPrestigeMetric += boardCompositionShift * WEIGHTS.boardCompositionShift.research; // State-focus might de-emphasize basic research
  researchPrestigeMetric += stateFundingPriorityChange * WEIGHTS.stateFundingPriorityChange.research;
  researchPrestigeMetric += (tuitionPolicyAutonomy - 50) * WEIGHTS.tuitionPolicyAutonomy.research;
  researchPrestigeMetric += endowmentUtilizationFocus * WEIGHTS.endowmentUtilizationFocus.research;
  researchPrestigeMetric += (researchAcademicBalance - 50) * WEIGHTS.researchAcademicBalance.research; // Direct impact
  researchPrestigeMetric += studentAidPolicyShift * WEIGHTS.studentAidPolicyShift.research; // Indirect, faculty often attracted to strong student bodies.

  researchPrestigeMetric += projectedAnnualFundingImpact / 1000 * 15; // Strong funding can directly boost research infrastructure and faculty hires.

  researchPrestigeMetric = Math.max(0, Math.min(100, researchPrestigeMetric));

  return {
    projectedAnnualFundingImpact: projectedAnnualFundingImpact,
    academicProgramInnovationIndex: academicProgramInnovationIndex,
    studentAccessAndSuccessIndex: studentAccessAndSuccessIndex,
    researchPrestigeMetric: researchPrestigeMetric
  };
}
