const normalizeLeaderScore = (score) => ((score - 1) / 4) * 100; // Maps 1-5 to 0-100

const normalizedLeaderAcademic = normalizeLeaderScore(leader_academic_performance_score);
const normalizedLeaderFinancial = normalizeLeaderScore(leader_financial_performance_score);
const normalizedLeaderReputational = normalizeLeaderScore(leader_reputational_performance_score);

// --- 1. Calculate Core Impact Baselines ---
// Academic Impact (Initial influence based on institutional trend and leader's past academic success)
let academicImpact = (institutional_academic_trend * 3.0) + (normalizedLeaderAcademic * 0.4);

// Financial Impact (Initial influence based on institutional trend and leader's past financial success)
let financialImpact = (institutional_financial_trend * 4.0) + (normalizedLeaderFinancial * 0.4);

// Reputational Impact (Initial influence based on institutional trend and leader's past reputational success)
let reputationalImpact = (institutional_reputational_trend * 2.5) + (normalizedLeaderReputational * 0.5);

// --- 2. Adjust for Strategic Alignment ---
// Alignment factor: Neutral (3) = 1.0. Higher alignment (5) increases impact, lower (1) decreases it.
const alignmentFactor = 1.0 + ((strategic_alignment_score - 3) * 0.08); // Range from 0.84 to 1.16
academicImpact *= alignmentFactor;
financialImpact *= alignmentFactor;
reputationalImpact *= alignmentFactor;

// --- 3. Adjust for Initial Investment/Divestment ---
// Investment primarily affects financial, with moderate spillover to academic and reputational.
const investmentEffectFinancial = initial_investment_plan_usd_millions * 0.3; // 0.3 score points per $M
const investmentEffectAcademic = initial_investment_plan_usd_millions * 0.05; // 0.05 score points per $M
const investmentEffectReputational = initial_investment_plan_usd_millions * 0.08; // 0.08 score points per $M

financialImpact += investmentEffectFinancial;
academicImpact += investmentEffectAcademic;
reputationalImpact += investmentEffectReputational;

// --- 4. Adjust for Stakeholder Confidence ---
// Confidence provides an overall positive or negative sentiment adjustment, weighted more towards reputational impact.
const confidenceAdjustment = (stakeholder_confidence_index - 50) * 0.2; // Ranges from -10 (0 index) to +10 (100 index)

academicImpact += confidenceAdjustment * 0.8; // Moderate influence
financialImpact += confidenceAdjustment * 0.9; // Stronger influence
reputationalImpact += confidenceAdjustment * 1.2; // Heaviest influence

// --- 5. Final Score Normalization and Capping (0-100) ---
// Raw scores can be positive or negative. We map them to a 0-100 scale, with 50 as a conceptual neutral point.
const capAndFloor = (value) => Math.max(0, Math.min(100, value + 50)); // Adds a baseline of 50 to raw score, then caps.

academicImpact = capAndFloor(academicImpact);
financialImpact = capAndFloor(financialImpact);
reputationalImpact = capAndFloor(reputationalImpact);

// --- 6. Calculate Overall Leadership Impact Score ---
// Weighted average of the three key impact areas.
const overallLeadershipImpact = (academicImpact * 0.35 + financialImpact * 0.35 + reputationalImpact * 0.30);

// Ensure overall score is also capped within 0-100
const finalOverallImpact = Math.max(0, Math.min(100, overallLeadershipImpact));

return {
    academic_impact_score: parseFloat(academicImpact.toFixed(1)),
    financial_impact_score: parseFloat(financialImpact.toFixed(1)),
    reputational_impact_score: parseFloat(reputationalImpact.toFixed(1)),
    overall_leadership_impact_score: parseFloat(finalOverallImpact.toFixed(1))
};
