function (inputs) {
    const { 
        initialAnnualResearchFunding, 
        fundingChangePercentage, 
        indirectCostRate, 
        directCostFacultySalaryRatio, 
        directCostInfrastructureRatio, 
        directCostStudentProgramsRatio, 
        avgResearchFacultySalary, 
        endowmentReallocationPercentage, 
        annualEndowmentIncome 
    } = inputs;

    // Input validation and default values
    const safeInitialFunding = Math.max(0, initialAnnualResearchFunding || 0);
    const safeFundingChangePercent = fundingChangePercentage !== undefined ? fundingChangePercentage : 0;
    const safeIndirectCostRate = Math.max(0, Math.min(100, indirectCostRate || 0)) / 100;
    const safeFacultyRatio = Math.max(0, Math.min(100, directCostFacultySalaryRatio || 0)) / 100;
    const safeInfrastructureRatio = Math.max(0, Math.min(100, directCostInfrastructureRatio || 0)) / 100;
    const safeStudentProgramsRatio = Math.max(0, Math.min(100, directCostStudentProgramsRatio || 0)) / 100;
    const safeAvgFacultySalary = Math.max(1, avgResearchFacultySalary || 1); // Avoid division by zero
    const safeEndowmentReallocationPercent = Math.max(0, Math.min(100, endowmentReallocationPercentage || 0)) / 100;
    const safeAnnualEndowmentIncome = Math.max(0, annualEndowmentIncome || 0);

    // Check if direct cost ratios exceed 100% (sum of faculty, infrastructure, student programs)
    const totalDirectCostRatio = safeFacultyRatio + safeInfrastructureRatio + safeStudentProgramsRatio;
    if (totalDirectCostRatio > 1.01) { // Allow for slight floating point inaccuracies
        throw new Error("Sum of direct cost allocation ratios (Faculty, Infrastructure, Student Programs) cannot exceed 100%.");
    }

    // Step 1: Calculate the change in total research funding
    const fundingChangeFactor = safeFundingChangePercent / 100;
    const totalFundingChange = safeInitialFunding * fundingChangeFactor;
    const newTotalResearchFunding = safeInitialFunding + totalFundingChange;

    // Step 2: Calculate the impact on indirect costs
    // Note: Indirect costs are typically applied to *direct costs*. However, for overall budget impact, we consider the portion of the change in funding that would have been indirect cost recovery.
    // We'll calculate the total funding change first, then the indirect component of that change.
    // Assuming initial funding was already composed of (1 - safeIndirectCostRate) Direct Costs and safeIndirectCostRate Indirect Costs
    const directCostsFromInitialFunding = safeInitialFunding / (1 + safeIndirectCostRate); // If initial funding includes indirects
    const indirectCostsFromInitialFunding = safeInitialFunding - directCostsFromInitialFunding;

    const projectedNewDirectCosts = newTotalResearchFunding / (1 + safeIndirectCostRate);
    const projectedNewIndirectCosts = newTotalResearchFunding - projectedNewDirectCosts;

    const indirectCostImpact = projectedNewIndirectCosts - indirectCostsFromInitialFunding;

    // Step 3: Calculate the impact on direct costs and their sub-allocations
    // The *change* in direct costs is derived from the *change* in total funding, adjusted for the indirect cost rate.
    const changeInDirectCosts = totalFundingChange / (1 + safeIndirectCostRate);
    const directCostImpact = changeInDirectCosts;

    const facultySalaryImpact = changeInDirectCosts * safeFacultyRatio;
    const infrastructureInvestmentImpact = changeInDirectCosts * safeInfrastructureRatio;
    const studentProgramImpact = changeInDirectCosts * safeStudentProgramsRatio;

    // Step 4: Estimate faculty equivalents affected
    let potentialFacultyEquivalentsAffected = 0;
    if (facultySalaryImpact !== 0 && safeAvgFacultySalary > 0) {
        potentialFacultyEquivalentsAffected = Math.abs(facultySalaryImpact) / safeAvgFacultySalary;
    }

    // Step 5: Calculate net budget impact before mitigation
    // This is the total funding change, which includes both direct and indirect components.
    const netBudgetImpactBeforeMitigation = totalFundingChange;

    // Step 6: Calculate mitigation from endowment reallocation
    const mitigationFromEndowment = safeAnnualEndowmentIncome * safeEndowmentReallocationPercent;

    // Step 7: Calculate net budget impact after mitigation
    const netBudgetImpactAfterMitigation = netBudgetImpactBeforeMitigation + mitigationFromEndowment;
    // Note: If funding decreases, totalFundingChange is negative. Adding mitigation (positive) reduces the negative impact.
    // If funding increases, totalFundingChange is positive. Adding mitigation increases available funds.

    return {
        newTotalResearchFunding: newTotalResearchFunding,
        totalFundingChange: totalFundingChange,
        indirectCostImpact: indirectCostImpact,
        directCostImpact: directCostImpact,
        facultySalaryImpact: facultySalaryImpact,
        potentialFacultyEquivalentsAffected: potentialFacultyEquivalentsAffected,
        infrastructureInvestmentImpact: infrastructureInvestmentImpact,
        studentProgramImpact: studentProgramImpact,
        netBudgetImpactBeforeMitigation: netBudgetImpactBeforeMitigation,
        mitigationFromEndowment: mitigationFromEndowment,
        netBudgetImpactAfterMitigation: netBudgetImpactAfterMitigation
    };
}
