function calculate({ programCost, programDurationMonths, currentAnnualSalary, lostIncomeDuringProgram, projectedSalaryIncreasePercentage, timeToAchieveIncreaseMonths, careerAdvancementValueAnnual, annualMarketSalaryGrowthRate, discountRate, projectionPeriodYears }) {
    // Input validation and default values
    programCost = Math.max(0, programCost || 0);
    programDurationMonths = Math.max(0, programDurationMonths || 0);
    currentAnnualSalary = Math.max(0, currentAnnualSalary || 0);
    lostIncomeDuringProgram = Math.max(0, lostIncomeDuringProgram || 0);
    projectedSalaryIncreasePercentage = Math.max(-100, projectedSalaryIncreasePercentage || 0); // Can be negative, but not below -100%
    timeToAchieveIncreaseMonths = Math.max(0, timeToAchieveIncreaseMonths || 0);
    careerAdvancementValueAnnual = Math.max(0, careerAdvancementValueAnnual || 0);
    annualMarketSalaryGrowthRate = Math.max(0, annualMarketSalaryGrowthRate || 0);
    discountRate = Math.max(0, discountRate || 0);
    projectionPeriodYears = Math.max(0, projectionPeriodYears || 0);

    const discountFactor = 1 + (discountRate / 100);
    const marketGrowthFactor = 1 + (annualMarketSalaryGrowthRate / 100);

    // 1. Calculate Total Program Investment
    const totalInvestment = programCost + lostIncomeDuringProgram;

    // 2. Calculate Projected Annual Post-Program Salary
    const postProgramBaseSalary = currentAnnualSalary * (1 + projectedSalaryIncreasePercentage / 100);
    const annualSalaryUpliftAbsolute = postProgramBaseSalary - currentAnnualSalary;

    // 3. Calculate effective annual benefit for initial break-even
    const effectiveAnnualBenefitForBreakeven = annualSalaryUpliftAbsolute + careerAdvancementValueAnnual;

    // 4. Calculate Break-Even Point
    let breakEvenPointYears = Infinity;
    if (totalInvestment > 0 && effectiveAnnualBenefitForBreakeven > 0) {
        breakEvenPointYears = totalInvestment / effectiveAnnualBenefitForBreakeven;
    } else if (totalInvestment <= 0 && effectiveAnnualBenefitForBreakeven > 0) {
        breakEvenPointYears = 0; // Immediate break-even if no cost and there's a benefit
    } else if (effectiveAnnualBenefitForBreakeven <= 0) {
        breakEvenPointYears = Infinity; // Will never break even if no net benefit
    }

    // 5. Project Cash Flows for Total Projected Gain and Net Present Value (NPV)
    let totalProjectedGainRaw = 0; // Sum of benefits without discounting
    let npv = -totalInvestment; // NPV starts with the initial investment as an outflow

    const yearsUntilFullBenefit = (programDurationMonths + timeToAchieveIncreaseMonths) / 12;

    for (let year = 1; year <= projectionPeriodYears; year++) {
        // Salary without upskilling, growing at market rate
        const earningsWithoutUpskillInYear = currentAnnualSalary * Math.pow(marketGrowthFactor, year - 1);

        // Salary with upskilling, considering ramp-up and then market growth
        let earningsWithUpskillInYear;
        if (year <= yearsUntilFullBenefit) {
            // During training/ramp-up, assume no additional salary uplift. Income is current salary
            // growing at market rate, as lost income was an upfront cost.
            earningsWithUpskillInYear = currentAnnualSalary * Math.pow(marketGrowthFactor, year - 1);
        } else {
            // After full benefit kicks in, the new base salary grows from there
            const yearsPostBenefitStart = year - yearsUntilFullBenefit;
            earningsWithUpskillInYear = postProgramBaseSalary * Math.pow(marketGrowthFactor, Math.max(0, yearsPostBenefitStart - 1));
        }

        // Calculate the incremental benefit for this year
        const annualBenefitIncremental = (earningsWithUpskillInYear - earningsWithoutUpskillInYear) + careerAdvancementValueAnnual;

        totalProjectedGainRaw += annualBenefitIncremental; // Accumulate undiscounted benefits
        npv += annualBenefitIncremental / Math.pow(discountFactor, year); // Accumulate discounted benefits
    }

    // 6. Calculate Return on Investment (ROI) Percentage
    let returnOnInvestmentPercentage = 0;
    if (totalInvestment > 0) {
        returnOnInvestmentPercentage = (totalProjectedGainRaw / totalInvestment) * 100;
    } else if (totalInvestment <= 0 && totalProjectedGainRaw > 0) {
        returnOnInvestmentPercentage = Infinity; // Infinite ROI if no investment and positive gain
    } else if (totalInvestment <= 0 && totalProjectedGainRaw <= 0) {
        returnOnInvestmentPercentage = 0; // No investment, no gain
    }

    return {
        totalInvestment: totalInvestment,
        projectedAnnualPostProgramSalary: postProgramBaseSalary,
        annualSalaryUplift: annualSalaryUpliftAbsolute,
        breakEvenPointYears: Math.max(0, breakEvenPointYears), // Ensure non-negative
        totalProjectedGain: totalProjectedGainRaw,
        netPresentValue: npv,
        returnOnInvestmentPercentage: returnOnInvestmentPercentage
    };
}
