function calculateScore(inputs) {
    const {
        microgridPenetration,
        backupPowerRedundancy,
        smartGridIntegration,
        cybersecurityScore,
        physicalHardening,
        emergencyResponse,
        energyStorageCapacity,
        interdependencyManagement
    } = inputs;

    // Input validation and normalization
    const mp = Math.max(0, Math.min(100, microgridPenetration)); // 0-100%
    const bpr = Math.max(0, Math.min(200, backupPowerRedundancy)); // 0-200%, cap at 150 for scoring effect
    const sgi = Math.max(1, Math.min(5, smartGridIntegration)); // 1-5
    const css = Math.max(1, Math.min(5, cybersecurityScore)); // 1-5
    const ph = Math.max(1, Math.min(5, physicalHardening)); // 1-5
    const er = Math.max(1, Math.min(5, emergencyResponse)); // 1-5
    const esc = Math.max(0, energyStorageCapacity); // MWh/100k residents
    const im = Math.max(1, Math.min(5, interdependencyManagement)); // 1-5

    // Define weights for each factor (sum to 1.0)
    const weights = {
        microgridPenetration: 0.18,
        backupPowerRedundancy: 0.22,
        smartGridIntegration: 0.15,
        cybersecurityScore: 0.12,
        physicalHardening: 0.10,
        emergencyResponse: 0.08,
        energyStorageCapacity: 0.10,
        interdependencyManagement: 0.05
    };

    // Calculate normalized component scores (max value for each is 100 * weight)
    const score_mp = (mp / 100) * (weights.microgridPenetration * 100);
    const score_bpr = (Math.min(bpr, 150) / 150) * (weights.backupPowerRedundancy * 100); // Capped at 150% for full contribution
    const score_sgi = ((sgi - 1) / 4) * (weights.smartGridIntegration * 100);
    const score_css = ((css - 1) / 4) * (weights.cybersecurityScore * 100);
    const score_ph = ((ph - 1) / 4) * (weights.physicalHardening * 100);
    const score_er = ((er - 1) / 4) * (weights.emergencyResponse * 100);
    const score_esc = (Math.min(esc, 100) / 100) * (weights.energyStorageCapacity * 100); // Assume 100 MWh/100k residents is excellent
    const score_im = ((im - 1) / 4) * (weights.interdependencyManagement * 100);

    // Sum all component scores for the total resilience score
    let resilienceScore = score_mp +
                          score_bpr +
                          score_sgi +
                          score_css +
                          score_ph +
                          score_er +
                          score_esc +
                          score_im;

    // Ensure score is between 0 and 100
    resilienceScore = Math.max(0, Math.min(100, resilienceScore));

    let tier = '';
    let recommendation = '';

    const componentScores = {
        microgridPenetration: score_mp,
        backupPowerRedundancy: score_bpr,
        smartGridIntegration: score_sgi,
        cybersecurityScore: score_css,
        physicalHardening: score_ph,
        emergencyResponse: score_er,
        energyStorageCapacity: score_esc,
        interdependencyManagement: score_im
    };

    const factorLabels = {
        microgridPenetration: 'Microgrid & DER Penetration',
        backupPowerRedundancy: 'Critical Load Backup Power Redundancy',
        smartGridIntegration: 'Smart Grid Integration',
        cybersecurityScore: 'Cybersecurity Resilience',
        physicalHardening: 'Physical Hardening Measures',
        emergencyResponse: 'Emergency Response & Restoration Capability',
        energyStorageCapacity: 'Grid-Scale Energy Storage Capacity',
        interdependencyManagement: 'Interdependency Management'
    };

    // Determine tier and generate recommendations
    if (resilienceScore <= 30) {
        tier = 'Critical Vulnerability';
        recommendation = 'Immediate and comprehensive investments in multiple resilience factors are required. Focus on establishing basic backup power and developing initial smart grid capabilities.';
    } else if (resilienceScore <= 50) {
        tier = 'Developing Resilience';
        recommendation = 'Significant improvements are needed. Prioritize investments in grid modernization, enhanced backup power, and cybersecurity.';
    } else if (resilienceScore <= 70) {
        tier = 'Moderate Resilience';
        recommendation = 'Continue to invest in strategic areas. Focus on expanding microgrid adoption, improving interdependency management, and enhancing physical security.';
    } else if (resilienceScore <= 85) {
        tier = 'Robust Resilience';
        recommendation = 'Maintain vigilance and seek continuous improvement. Explore advanced smart grid features and innovative energy storage solutions.';
    } else {
        tier = 'Leading Resilience';
        recommendation = 'Your urban area demonstrates exceptional resilience. Focus on cutting-edge research, adaptive strategies for evolving threats, and serving as a model for other communities.';
    }

    // Refine recommendation based on lowest-scoring factors if not already in Critical/Leading
    if (resilienceScore > 30 && resilienceScore < 86) {
        const sortedFactors = Object.keys(componentScores).sort((a, b) => componentScores[a] - componentScores[b]);
        const lowestFactor = sortedFactors[0];
        const secondLowestFactor = sortedFactors[1];

        if (componentScores[lowestFactor] / (weights[lowestFactor] * 100) < 0.3) { // If lowest factor is very low (less than 30% of its max contribution)
            recommendation = `Consider immediate action on improving ${factorLabels[lowestFactor]}. Your current score indicates this is a primary vulnerability.`;
        } else if (componentScores[lowestFactor] / (weights[lowestFactor] * 100) < 0.5) { // If lowest factor is moderately low
            recommendation = `A key area for improvement is ${factorLabels[lowestFactor]}. Strengthening this component would significantly boost overall resilience.`;
        } else if (componentScores[lowestFactor] / (weights[lowestFactor] * 100) < 0.7 && componentScores[secondLowestFactor] / (weights[secondLowestFactor] * 100) < 0.7) {
            recommendation = `Focus on optimizing ${factorLabels[lowestFactor]} and ${factorLabels[secondLowestFactor]} to further enhance your resilience profile.`;
        }
    }

    return {
        resilienceScore: parseFloat(resilienceScore.toFixed(1)),
        tier: tier,
        recommendation: recommendation
    };
}
