formula:
function calculate({ projectArea, initialCostPerSqM, annualMaintenanceCostPerSqM, projectLifespan, discountRate, stormwaterValuePerSqM, energySavingsValuePerSqM, airQualityValuePerSqM, propertyValueUpliftFactor, socialHealthValuePerSqM }) {

    // Input Validation and Edge Cases
    if (projectArea <= 0 || projectLifespan <= 0) {
        return {
            totalInitialInvestment: 0,
            totalAnnualMaintenanceCost: 0,
            totalAnnualEnvironmentalBenefits: 0,
            totalAnnualEconomicBenefits: 0,
            totalAnnualSocialBenefits: 0,
            totalAnnualGrossBenefits: 0,
            totalAnnualNetBenefit: 0,
            simplePaybackPeriod: 'N/A',
            netPresentValue: 'N/A',
            returnOnInvestmentPercentage: 'N/A'
        };
    }

    const effectiveDiscountRate = discountRate / 100;

    // 1. Calculate Base Costs and Benefits
    const totalInitialInvestment = projectArea * initialCostPerSqM;
    const totalAnnualMaintenanceCost = projectArea * annualMaintenanceCostPerSqM;

    const annualEnvironmentalBenefitsPerSqM = stormwaterValuePerSqM + energySavingsValuePerSqM + airQualityValuePerSqM;
    const totalAnnualEnvironmentalBenefits = projectArea * annualEnvironmentalBenefitsPerSqM;

    // Property value uplift: Assume this factor applies to the total initial investment value (land+building) that the GI affects.
    // For simplicity, let's apply it to a base assumed property value per sq meter, or to a general market value influenced by GI.
    // A more robust model would need a specific property value input.
    // For this calculator, let's interpret propertyValueUpliftFactor as an annual monetized benefit per sq meter.
    // If it's a factor, it should be applied to an existing property value, which we don't have as an input.
    // Let's assume propertyValueUpliftFactor is effectively a benefit *rate* per year per sq meter *of value created*.
    // For simplicity, let's assume it represents an annual benefit derived from property value increase per sq meter of GI area.
    const totalAnnualEconomicBenefits = projectArea * (propertyValueUpliftFactor * initialCostPerSqM); // A simple way to monetize uplift

    const totalAnnualSocialBenefits = projectArea * socialHealthValuePerSqM;

    const totalAnnualGrossBenefits = totalAnnualEnvironmentalBenefits + totalAnnualEconomicBenefits + totalAnnualSocialBenefits;
    const totalAnnualNetBenefit = totalAnnualGrossBenefits - totalAnnualMaintenanceCost;

    // 2. Simple Payback Period
    let simplePaybackPeriod;
    if (totalAnnualNetBenefit <= 0) {
        simplePaybackPeriod = 'N/A (No Positive Net Benefit)';
    } else {
        simplePaybackPeriod = totalInitialInvestment / totalAnnualNetBenefit;
        if (simplePaybackPeriod > projectLifespan) {
            simplePaybackPeriod = `>${projectLifespan} (No Payback within Lifespan)`;
        }
    }

    // 3. Net Present Value (NPV)
    let netPresentValue = -totalInitialInvestment; // Start with initial investment as a negative cash flow
    for (let t = 1; t <= projectLifespan; t++) {
        netPresentValue += totalAnnualNetBenefit / Math.pow(1 + effectiveDiscountRate, t);
    }

    // 4. Return on Investment (ROI)
    let returnOnInvestmentPercentage;
    if (totalInitialInvestment === 0) {
        returnOnInvestmentPercentage = 'N/A (Zero Initial Investment)';
    } else {
        // ROI = (Total Lifetime Net Benefits - Initial Investment) / Initial Investment * 100
        // Total Lifetime Net Benefits = Sum of (Annual Net Benefit / (1 + r)^t)
        // Which is basically NPV + Initial Investment
        returnOnInvestmentPercentage = (netPresentValue / totalInitialInvestment) * 100;
    }

    // Cap ROI if it's extremely high due to very low initial investment or very high benefits
    if (typeof returnOnInvestmentPercentage === 'number' && returnOnInvestmentPercentage > 1000000) {
        returnOnInvestmentPercentage = '> 1,000,000';
    }

    return {
        totalInitialInvestment: totalInitialInvestment,
        totalAnnualMaintenanceCost: totalAnnualMaintenanceCost,
        totalAnnualEnvironmentalBenefits: totalAnnualEnvironmentalBenefits,
        totalAnnualEconomicBenefits: totalAnnualEconomicBenefits,
        totalAnnualSocialBenefits: totalAnnualSocialBenefits,
        totalAnnualGrossBenefits: totalAnnualGrossBenefits,
        totalAnnualNetBenefit: totalAnnualNetBenefit,
        simplePaybackPeriod: simplePaybackPeriod,
        netPresentValue: netPresentValue,
        returnOnInvestmentPercentage: returnOnInvestmentPercentage
    };
}
