function calculate() {
    const proximityToGreenSpace = inputs.proximityToGreenSpace;
    const greenSpaceQualityRating = inputs.greenSpaceQualityRating;
    const weeklyVisitsFrequency = inputs.weeklyVisitsFrequency;
    const averageVisitDurationMinutes = inputs.averageVisitDurationMinutes;
    const perceivedSafetyScore = inputs.perceivedSafetyScore;
    const biodiversityRichnessScore = inputs.biodiversityRichnessScore;
    const canopyCoverPercentage = inputs.canopyCoverPercentage;
    const noiseLevelImpact = inputs.noiseLevelImpact;

    // --- Input Validation & Clamping ---
    const clampedProximity = Math.max(0, proximityToGreenSpace);
    const clampedQuality = Math.max(1, Math.min(5, greenSpaceQualityRating));
    const clampedVisits = Math.max(0, weeklyVisitsFrequency);
    const clampedDuration = Math.max(0, averageVisitDurationMinutes);
    const clampedSafety = Math.max(1, Math.min(5, perceivedSafetyScore));
    const clampedBiodiversity = Math.max(1, Math.min(5, biodiversityRichnessScore));
    const clampedCanopy = Math.max(0, Math.min(100, canopyCoverPercentage));
    const clampedNoise = Math.max(1, Math.min(5, noiseLevelImpact));

    // --- Component Calculations ---

    // 1. Proximity Impact Factor (inverse relationship, higher for closer proximity)
    // Optimal up to 200m (factor 1.0), linearly decreases to 0.5 at 1000m, then slower decrease.
    let proximityFactor;
    if (clampedProximity <= 200) {
        proximityFactor = 1.0;
    } else if (clampedProximity <= 1000) {
        proximityFactor = 1.0 - (clampedProximity - 200) / 800 * 0.5; 
    } else {
        proximityFactor = 0.5 - (clampedProximity - 1000) / 1000 * 0.4; 
    }
    proximityFactor = Math.max(0.1, Math.min(1.0, proximityFactor)); // Cap between 0.1 and 1.0

    // 2. Quality & Safety Factor (direct positive impact)
    // A weighted average, scaled to contribute significantly.
    let qualitySafetyFactor = (clampedQuality * 0.6 + clampedSafety * 0.4);
    qualitySafetyFactor = Math.max(1, Math.min(5, qualitySafetyFactor)); // Scale 1-5

    // 3. Utilization Factor (frequency and duration, with diminishing returns for excessive exposure)
    const totalWeeklyExposureMinutes = clampedVisits * clampedDuration;
    // Benefits plateau around 150 minutes/week (often cited recommendation).
    let utilizationFactor = Math.min(1, totalWeeklyExposureMinutes / 150);
    utilizationFactor = Math.pow(utilizationFactor, 0.7); // Apply a power function for diminishing returns

    // 4. Environmental Richness Factor (biodiversity and canopy cover)
    let richnessFactor = (clampedBiodiversity * 0.7 + (clampedCanopy / 100) * 5 * 0.3);
    richnessFactor = Math.max(1, Math.min(5, richnessFactor)); // Scale 1-5

    // 5. Noise Impact Penalty (negative impact, higher noise = higher penalty)
    let noisePenalty = (clampedNoise - 1) * 0.25; // 0 for noise 1, up to 1 for noise 5
    noisePenalty = Math.max(0, Math.min(1, noisePenalty));

    // --- Overall Mental Health Benefit Score (0-100) ---
    // Base score from quality, safety, and utilization, enhanced by richness, adjusted by proximity, and penalized by noise.

    let coreBenefit = (qualitySafetyFactor / 5) * (utilizationFactor * 0.6 + 0.4); 
    coreBenefit = coreBenefit * 0.6 + (richnessFactor / 5) * 0.4; 
    coreBenefit = Math.max(0.1, Math.min(1, coreBenefit)); 

    let overallScore = coreBenefit * proximityFactor * 100; 
    overallScore = overallScore * (1 - noisePenalty * 0.5); // Max 50% reduction from noise

    // Final capping for the overall score
    overallScore = Math.max(10, Math.min(100, overallScore)); 

    // --- Stress Reduction Potential Index (0-10) ---
    // Higher quality, safety, duration, and lower noise contribute more.
    let stressReductionIndex = (clampedQuality / 5) * 3 + (clampedSafety / 5) * 2 +
                               (utilizationFactor * 0.7 + 0.3) * 3 + (1 - noisePenalty) * 2;
    stressReductionIndex = Math.max(1, Math.min(10, stressReductionIndex));

    // --- Mood Enhancement Potential Index (0-10) ---
    // Higher visits, duration, biodiversity, canopy cover, and quality contribute more.
    let moodEnhancementIndex = (clampedQuality / 5) * 3 + (clampedBiodiversity / 5) * 2 +
                               (clampedCanopy / 100) * 2 + (utilizationFactor * 0.8 + 0.2) * 3;
    moodEnhancementIndex = Math.max(1, Math.min(10, moodEnhancementIndex));

    return {
        overallMentalHealthBenefitScore: Math.round(overallScore),
        stressReductionIndex: parseFloat(stressReductionIndex.toFixed(1)),
        moodEnhancementIndex: parseFloat(moodEnhancementIndex.toFixed(1))
    };
}
