const p_proximity = parseFloat(inputs.proximity_to_green_space_meters);
const p_coverage = parseFloat(inputs.green_space_coverage_percent);
const p_biodiversity = parseFloat(inputs.biodiversity_score);
const p_maintenance = parseFloat(inputs.maintenance_score);
const p_amenity = parseFloat(inputs.amenity_score);
const p_type = inputs.green_space_type;
const p_duration = parseFloat(inputs.typical_visit_duration_minutes);
const p_frequency = parseFloat(inputs.typical_visit_frequency_per_week);

let score = 0;

// 1. Proximity Score (max 20 points)
// Shorter distance = higher score, diminishing returns after 100m
if (!isNaN(p_proximity)) {
    if (p_proximity <= 100) score += 20;
    else if (p_proximity <= 250) score += 15;
    else if (p_proximity <= 500) score += 10;
    else if (p_proximity <= 1000) score += 5;
    // else score remains 0 (for >1000m, minimal proximity impact)
}

// 2. Green Space Coverage Score (max 15 points)
// Linear contribution. Higher coverage = higher score.
if (!isNaN(p_coverage)) {
    score += Math.min(15, Math.max(0, p_coverage * 0.15));
}

// 3. Biodiversity Score (max 10 points)
// Direct mapping from 1-5 to 2-10.
if (!isNaN(p_biodiversity)) {
    score += Math.min(10, Math.max(0, p_biodiversity * 2));
}

// 4. Maintenance & Cleanliness Score (max 10 points)
// Direct mapping from 1-5 to 2-10.
if (!isNaN(p_maintenance)) {
    score += Math.min(10, Math.max(0, p_maintenance * 2));
}

// 5. Amenity Score (max 10 points)
// Direct mapping from 1-5 to 2-10.
if (!isNaN(p_amenity)) {
    score += Math.min(10, Math.max(0, p_amenity * 2));
}

// 6. Green Space Type Score (max 15 points)
let type_score = 0;
switch (p_type) {
    case "Forest/Woodland": type_score = 15; break;
    case "Riverfront/Waterside Park": type_score = 14; break;
    case "Urban Park": type_score = 12; break;
    case "Community Garden": type_score = 10; break;
    case "Cemetery with Greenery": type_score = 8; break;
    case "Urban Plaza with Greenery": type_score = 6; break;
    case "Pocket Park": type_score = 5; break;
    default: type_score = 0; // Unknown or not specified
}
score += type_score;

// 7. Visit Duration Score (max 10 points)
// More duration = higher score, diminishing returns after ~150 minutes (2.5 hours).
if (!isNaN(p_duration)) {
    score += Math.min(10, Math.max(0, p_duration / 15)); 
}

// 8. Visit Frequency Score (max 10 points)
// More frequent = higher score, diminishing returns after daily visits.
if (!isNaN(p_frequency)) {
    score += Math.min(10, Math.max(0, p_frequency * 1.5));
}

// Ensure the final score is within the 0-100 range
const final_score = Math.min(100, Math.max(0, score));

let benefit_level = "Undetermined";
if (final_score >= 0 && final_score <= 20) {
    benefit_level = "Minimal Impact";
} else if (final_score > 20 && final_score <= 40) {
    benefit_level = "Low Impact";
} else if (final_score > 40 && final_score <= 60) {
    benefit_level = "Moderate Impact";
} else if (final_score > 60 && final_score <= 80) {
    benefit_level = "High Impact";
} else if (final_score > 80 && final_score <= 100) {
    benefit_level = "Excellent Impact";
}

return {
    mental_health_impact_score: parseFloat(final_score.toFixed(1)),
    benefit_level: benefit_level
};
