function calculate({ cityAreaSqKm, avgSummerTempC, avgCoolingEnergyIntensityKwhM2Yr, percentGreenRoofs, percentCoolPavements, percentTreeCanopy, gridEmissionFactorKgCo2PerKwh }) {
  // Input validation and default values
  cityAreaSqKm = parseFloat(cityAreaSqKm) || 0;
  avgSummerTempC = parseFloat(avgSummerTempC) || 0;
  avgCoolingEnergyIntensityKwhM2Yr = parseFloat(avgCoolingEnergyIntensityKwhM2Yr) || 0;
  percentGreenRoofs = Math.max(0, Math.min(100, parseFloat(percentGreenRoofs) || 0));
  percentCoolPavements = Math.max(0, Math.min(100, parseFloat(percentCoolPavements) || 0));
  percentTreeCanopy = Math.max(0, Math.min(100, parseFloat(percentTreeCanopy) || 0));
  gridEmissionFactorKgCo2PerKwh = parseFloat(gridEmissionFactorKgCo2PerKwh) || 0.4; // Default to US average if not provided

  // Constants for mitigation effectiveness (assumed ambient temperature reduction per 100% coverage)
  // These values are generalized and can vary significantly based on local climate, specific materials, etc.
  const GREEN_ROOF_TEMP_REDUCTION_FACTOR_C = 1.8; // e.g., 1.8°C for 100% coverage
  const COOL_PAVEMENT_TEMP_REDUCTION_FACTOR_C = 2.0; // e.g., 2.0°C for 100% coverage
  const TREE_CANOPY_TEMP_REDUCTION_FACTOR_C = 2.5; // e.g., 2.5°C for 100% coverage (due to shade & evapotranspiration)

  // Cooling energy demand reduction per degree Celsius reduction (empirical, often 5-10%)
  const ENERGY_SAVINGS_PER_DEGREE_REDUCTION_PERCENT = 7; // 7% reduction in cooling energy per 1°C ambient temp drop

  // Average energy cost per kWh (illustrative, for USD calculation)
  const AVG_ENERGY_COST_PER_KWH_USD = 0.15; // e.g., 15 cents/kWh

  // Calculate weighted average temperature reduction based on coverage and factors
  let potentialTempReductionC = 
    (percentGreenRoofs / 100 * GREEN_ROOF_TEMP_REDUCTION_FACTOR_C) + 
    (percentCoolPavements / 100 * COOL_PAVEMENT_TEMP_REDUCTION_FACTOR_C) + 
    (percentTreeCanopy / 100 * TREE_CANOPY_TEMP_REDUCTION_FACTOR_C);

  // Cap potential temperature reduction to a reasonable maximum (e.g., 5°C), as linear extrapolation has limits
  potentialTempReductionC = Math.min(potentialTempReductionC, 5.0); 

  const estimatedNewSummerTempC = avgSummerTempC - potentialTempReductionC;

  // Calculate total existing cooling energy demand for the urban area
  // Assume 70% of urban area is built environment requiring cooling
  const builtUpAreaSqM = cityAreaSqKm * 1000000 * 0.7; 
  const totalExistingCoolingDemandKwhPerYear = builtUpAreaSqM * avgCoolingEnergyIntensityKwhM2Yr;

  // Calculate cooling energy savings
  const percentEnergySavings = potentialTempReductionC * ENERGY_SAVINGS_PER_DEGREE_REDUCTION_PERCENT; // e.g., 1°C -> 7% savings
  const totalAnnualEnergySavingsKwh = totalExistingCoolingDemandKwhPerYear * (percentEnergySavings / 100);
  const totalAnnualEnergySavingsMwh = totalAnnualEnergySavingsKwh / 1000;

  // Calculate energy cost savings
  const totalAnnualEnergySavingsUsd = totalAnnualEnergySavingsKwh * AVG_ENERGY_COST_PER_KWH_USD;

  // Calculate CO2 reduction
  const totalAnnualCo2ReductionKg = totalAnnualEnergySavingsKwh * gridEmissionFactorKgCo2PerKwh;
  const totalAnnualCo2ReductionTonnes = totalAnnualCo2ReductionKg / 1000;

  // Handle edge cases where inputs might lead to non-sensical outputs
  if (cityAreaSqKm <= 0 || avgSummerTempC <= 0 || avgCoolingEnergyIntensityKwhM2Yr <= 0 || (percentGreenRoofs === 0 && percentCoolPavements === 0 && percentTreeCanopy === 0)) {
    potentialTempReductionC = 0;
    totalAnnualEnergySavingsKwh = 0;
    totalAnnualEnergySavingsMwh = 0;
    totalAnnualEnergySavingsUsd = 0;
    totalAnnualCo2ReductionTonnes = 0;
  }

  return {
    potentialTempReductionC: parseFloat(potentialTempReductionC.toFixed(2)),
    estimatedNewSummerTempC: parseFloat(estimatedNewSummerTempC.toFixed(2)),
    totalAnnualEnergySavingsMwh: parseFloat(totalAnnualEnergySavingsMwh.toFixed(2)),
    totalAnnualEnergySavingsUsd: parseFloat(totalAnnualEnergySavingsUsd.toFixed(2)),
    totalAnnualCo2ReductionTonnes: parseFloat(totalAnnualCo2ReductionTonnes.toFixed(2))
  };
}
