function calculateROI(inputs) {
    const {
        projectArea,
        strategyInitialCostPerUnit,
        expectedLifespan,
        avgAnnualCoolingEnergySavingsPercentage,
        avgAnnualHeatingEnergySavingsPercentage,
        avgEnergyCostPerKWH,
        avgKWHConsumptionPerSqFtAnnually,
        avgAnnualHealthBenefitValuePerSqFt,
        infrastructureLongevityIncreaseYears,
        infrastructureReplacementCostPerUnit,
        discountRate
    } = inputs;

    // Validate core inputs
    if (projectArea <= 0 || expectedLifespan <= 0 || strategyInitialCostPerUnit < 0 || discountRate < 0) {
        throw new Error("Project area, expected lifespan, initial strategy cost, and discount rate must be positive or non-negative values.");
    }

    const effectiveCoolingSavings = Math.min(Math.max(avgAnnualCoolingEnergySavingsPercentage, 0), 1); // Cap between 0 and 1
    const effectiveHeatingSavings = Math.min(Math.max(avgAnnualHeatingEnergySavingsPercentage, 0), 1); // Cap between 0 and 1
    const effectiveDiscountRate = Math.max(discountRate, 0); // Ensure non-negative discount rate for calculation

    // 1. Calculate Total Initial Investment
    const totalInitialInvestment = projectArea * strategyInitialCostPerUnit;

    // 2. Calculate Annual Energy Savings
    const baselineAnnualEnergyCostPerSqFt = avgKWHConsumptionPerSqFtAnnually * avgEnergyCostPerKWH;
    const annualEnergySavingsPerSqFt = baselineAnnualEnergyCostPerSqFt * (effectiveCoolingSavings + effectiveHeatingSavings);
    const annualEnergySavings = projectArea * annualEnergySavingsPerSqFt;

    // 3. Calculate Annual Health Benefits
    const annualHealthBenefits = projectArea * avgAnnualHealthBenefitValuePerSqFt;

    // 4. Calculate Annual Infrastructure Longevity Savings
    let annualInfrastructureSavings = 0;
    if (infrastructureLongevityIncreaseYears > 0 && infrastructureReplacementCostPerUnit > 0) {
        // Annualized benefit of deferring/reducing replacement cost due to extended lifespan.
        // Assumes the full replacement cost is distributed over the years of extended life provided.
        annualInfrastructureSavings = (projectArea * infrastructureReplacementCostPerUnit) / infrastructureLongevityIncreaseYears;
        // Ensure savings don't exceed the total potential replacement cost within a single year
        annualInfrastructureSavings = Math.min(annualInfrastructureSavings, projectArea * infrastructureReplacementCostPerUnit);
    } else if (infrastructureLongevityIncreaseYears === 0 && infrastructureReplacementCostPerUnit > 0) {
        // If lifespan increase is 0, no annual savings from this specific benefit
        annualInfrastructureSavings = 0;
    }

    // 5. Calculate Total Annual Benefits
    const totalAnnualBenefits = annualEnergySavings + annualHealthBenefits + annualInfrastructureSavings;

    // Handle edge case: No benefits but there's an investment
    if (totalAnnualBenefits <= 0 && totalInitialInvestment > 0) {
        return {
            totalInitialInvestment: totalInitialInvestment,
            annualEnergySavings: annualEnergySavings,
            annualHealthBenefits: annualHealthBenefits,
            annualInfrastructureSavings: annualInfrastructureSavings,
            totalAnnualBenefits: totalAnnualBenefits,
            paybackPeriod: Infinity, // Will never pay back
            totalBenefitsOverLifespan: 0,
            roiPercentage: -100, // Or similar indication of loss for initial investment
            netPresentValue: -totalInitialInvestment // Immediate loss of initial investment
        };
    }

    // 6. Calculate Simple Payback Period
    const paybackPeriod = totalInitialInvestment > 0 && totalAnnualBenefits > 0 ? totalInitialInvestment / totalAnnualBenefits : (totalInitialInvestment === 0 && totalAnnualBenefits > 0 ? 0 : Infinity);

    // 7. Calculate Total Benefits Over Strategy Lifespan (cumulative, not discounted)
    const totalBenefitsOverLifespan = totalAnnualBenefits * expectedLifespan;

    // 8. Calculate ROI Percentage
    const roiPercentage = totalInitialInvestment > 0 ? ((totalBenefitsOverLifespan - totalInitialInvestment) / totalInitialInvestment) * 100 : (totalBenefitsOverLifespan > 0 ? Infinity : 0);

    // 9. Calculate Net Present Value (NPV)
    let netPresentValue = -totalInitialInvestment; // Start with initial investment as a cost
    if (effectiveDiscountRate === 0) {
        netPresentValue += totalAnnualBenefits * expectedLifespan;
    } else {
        for (let i = 1; i <= expectedLifespan; i++) {
            netPresentValue += totalAnnualBenefits / Math.pow(1 + effectiveDiscountRate, i);
        }
    }

    return {
        totalInitialInvestment: totalInitialInvestment,
        annualEnergySavings: annualEnergySavings,
        annualHealthBenefits: annualHealthBenefits,
        annualInfrastructureSavings: annualInfrastructureSavings,
        totalAnnualBenefits: totalAnnualBenefits,
        paybackPeriod: paybackPeriod,
        totalBenefitsOverLifespan: totalBenefitsOverLifespan,
        roiPercentage: roiPercentage,
        netPresentValue: netPresentValue
    };
}
