formula:
function calculate({ projectAreaSqM, mitigatedSurfaceCoveragePercent, numTreesToPlant, averageSurfaceMitigationCostPerSqM, treePlantingCostPerTree, annualEnergySavingsPerSqM, carbonSequestrationPerTreeKg, carbonPricePerTonUSD, projectLifespanYears }) {
    // Ensure all inputs are non-negative
    projectAreaSqM = Math.max(0, projectAreaSqM);
    mitigatedSurfaceCoveragePercent = Math.max(0, Math.min(100, mitigatedSurfaceCoveragePercent));
    numTreesToPlant = Math.max(0, numTreesToPlant);
    averageSurfaceMitigationCostPerSqM = Math.max(0, averageSurfaceMitigationCostPerSqM);
    treePlantingCostPerTree = Math.max(0, treePlantingCostPerTree);
    annualEnergySavingsPerSqM = Math.max(0, annualEnergySavingsPerSqM);
    carbonSequestrationPerTreeKg = Math.max(0, carbonSequestrationPerTreeKg);
    carbonPricePerTonUSD = Math.max(0, carbonPricePerTonUSD);
    projectLifespanYears = Math.max(1, projectLifespanYears); // Lifespan must be at least 1 year

    const mitigatedAreaSqM = projectAreaSqM * (mitigatedSurfaceCoveragePercent / 100);

    // 1. Calculate Total Initial Investment
    const surfaceMitigationCost = mitigatedAreaSqM * averageSurfaceMitigationCostPerSqM;
    const treePlantingCost = numTreesToPlant * treePlantingCostPerTree;
    const totalInitialInvestment = surfaceMitigationCost + treePlantingCost;

    // 2. Calculate Annual Energy Savings
    const annualEnergySavings = mitigatedAreaSqM * annualEnergySavingsPerSqM;

    // 3. Calculate Annual Carbon Sequestration Value
    const annualCarbonSequestrationKg = numTreesToPlant * carbonSequestrationPerTreeKg;
    const annualCarbonSequestrationTons = annualCarbonSequestrationKg / 1000; // Convert kg to metric tons
    const annualCarbonSavingsValue = annualCarbonSequestrationTons * carbonPricePerTonUSD;

    // 4. Calculate Total Annual Financial Benefits
    const totalAnnualBenefits = annualEnergySavings + annualCarbonSavingsValue;

    // 5. Calculate Payback Period
    let paybackPeriodYears = Infinity;
    if (totalInitialInvestment > 0 && totalAnnualBenefits > 0) {
        paybackPeriodYears = totalInitialInvestment / totalAnnualBenefits;
    } else if (totalInitialInvestment === 0) {
        paybackPeriodYears = 0; // Immediate payback if no investment
    }

    // 6. Calculate Total Environmental Benefit (CO2 Sequestered)
    const totalEnvironmentalBenefitCO2Tons = annualCarbonSequestrationTons * projectLifespanYears;

    // 7. Calculate Return on Investment (ROI)
    let roiPercentage = 0;
    if (totalInitialInvestment > 0) {
        const totalBenefitOverLifespan = totalAnnualBenefits * projectLifespanYears;
        roiPercentage = ((totalBenefitOverLifespan - totalInitialInvestment) / totalInitialInvestment) * 100;
    } else if (totalAnnualBenefits > 0) {
        roiPercentage = Infinity; // Infinite ROI if benefits with no investment
    } else {
        roiPercentage = 0; // No ROI if no investment and no benefits
    }

    return {
        totalInitialInvestment,
        annualEnergySavings,
        annualCarbonSavingsValue,
        totalAnnualBenefits,
        paybackPeriodYears: isFinite(paybackPeriodYears) ? paybackPeriodYears : null, // Return null for N/A
        totalEnvironmentalBenefitCO2Tons,
        roiPercentage: isFinite(roiPercentage) ? roiPercentage : null // Return null for N/A
    };
}
