function calculate() {
  const propertyAcquisitionCost = parseFloat(inputs.propertyAcquisitionCost);
  const demolitionAndSitePrepCost = parseFloat(inputs.demolitionAndSitePrepCost);
  const newConstructionCostPerSF = parseFloat(inputs.newConstructionCostPerSF);
  const totalBuildableAreaSF = parseFloat(inputs.totalBuildableAreaSF);
  const averageSalesPricePerSF = parseFloat(inputs.averageSalesPricePerSF);
  const projectSoftCostsPercentage = parseFloat(inputs.projectSoftCostsPercentage);
  const financingCostInterestRateAnnual = parseFloat(inputs.financingCostInterestRateAnnual);
  const loanToCostRatio = parseFloat(inputs.loanToCostRatio);
  const projectDurationMonths = parseFloat(inputs.projectDurationMonths);
  const contingencyPercentage = parseFloat(inputs.contingencyPercentage);

  // Input validation and edge cases
  if ([propertyAcquisitionCost, demolitionAndSitePrepCost, newConstructionCostPerSF, totalBuildableAreaSF, averageSalesPricePerSF, projectSoftCostsPercentage, financingCostInterestRateAnnual, loanToCostRatio, projectDurationMonths, contingencyPercentage].some(isNaN) ||
      [propertyAcquisitionCost, demolitionAndSitePrepCost, newConstructionCostPerSF, totalBuildableAreaSF, averageSalesPricePerSF, projectSoftCostsPercentage, financingCostInterestRateAnnual, loanToCostRatio, projectDurationMonths, contingencyPercentage].some(val => val < 0)) {
    return { error: 'Please enter valid positive numbers for all inputs.' };
  }

  if (totalBuildableAreaSF <= 0 || averageSalesPricePerSF <= 0) {
      return { error: 'Total Buildable Area and Average Sales Price per SF must be greater than zero to calculate revenue.' };
  }

  if (loanToCostRatio > 100 || loanToCostRatio < 0) {
      return { error: 'Loan-to-Cost Ratio must be between 0% and 100%.' };
  }

  // 1. Calculate Total Hard Costs
  const totalHardCosts = newConstructionCostPerSF * totalBuildableAreaSF;

  // 2. Calculate Subtotal Project Cost (excluding softs, financing, contingency initially)
  const subtotalProjectCost = propertyAcquisitionCost + demolitionAndSitePrepCost + totalHardCosts;

  // 3. Calculate Soft Costs
  const softCosts = subtotalProjectCost * (projectSoftCostsPercentage / 100);

  // 4. Calculate Contingency
  const contingencyBase = subtotalProjectCost + softCosts;
  const contingencyAmount = contingencyBase * (contingencyPercentage / 100);

  // 5. Calculate Total Project Cost (pre-financing)
  const totalProjectCostIncludingSoftsAndContingency = subtotalProjectCost + softCosts + contingencyAmount;

  // 6. Calculate Loan Amount
  const loanAmount = totalProjectCostIncludingSoftsAndContingency * (loanToCostRatio / 100);

  // 7. Calculate Equity Required
  const equityRequired = totalProjectCostIncludingSoftsAndContingency - loanAmount;

  // 8. Calculate Total Financing Cost (simplified: interest-only on full loan amount for duration)
  const totalFinancingCost = loanAmount * (financingCostInterestRateAnnual / 100) * (projectDurationMonths / 12);

  // 9. Calculate Total Capital Deployed (Total Project Cost + Financing Costs)
  const totalCapitalDeployed = totalProjectCostIncludingSoftsAndContingency + totalFinancingCost;

  // 10. Calculate Total Project Revenue
  const totalProjectRevenue = averageSalesPricePerSF * totalBuildableAreaSF;

  // 11. Calculate Net Profit
  const netProfit = totalProjectRevenue - totalCapitalDeployed;

  // 12. Calculate Projected ROI (Cash-on-Cash for equity invested)
  let projectedROI = 0;
  if (equityRequired > 0) {
    projectedROI = (netProfit / equityRequired) * 100;
  } else if (equityRequired <= 0 && netProfit > 0) {
    projectedROI = Infinity; // Project generates profit with no or negative equity, very high ROI
  } else if (equityRequired <= 0 && netProfit <= 0) {
      projectedROI = 0; // No equity and no profit
  }

  // 13. Calculate Profit Margin
  let profitMargin = 0;
  if (totalProjectRevenue > 0) {
    profitMargin = (netProfit / totalProjectRevenue) * 100;
  }

  return {
    totalHardCosts: totalHardCosts,
    totalProjectCostIncludingSoftsAndContingency: totalProjectCostIncludingSoftsAndContingency,
    totalFinancingCost: totalFinancingCost,
    totalCapitalDeployed: totalCapitalDeployed,
    equityRequired: equityRequired,
    totalProjectRevenue: totalProjectRevenue,
    netProfit: netProfit,
    projectedROI: projectedROI,
    profitMargin: profitMargin
  };
}
