const { initialProjectCost, annualOperatingCost, projectLifespan, annualEconomicBenefit, annualPropertyValueBenefit, annualCommunityBenefitValue, discountRate, initialEconomicStimulus } = inputs; 

  let drDecimal = discountRate / 100; 

  // Ensure discount rate is positive for calculations; if 0 or negative, use a nominal small value or treat as 0 for no discounting. 
  // For simplicity and typical financial practice, a 0% discount rate means no discounting, so discountFactor becomes 1. 
  // A negative discount rate is generally not applicable for ROI calculations as it implies future money is worth more. 
  // We'll cap it at 0.0001 for mathematical stability if the user inputs 0 or less than 0. 
  const effectiveDrDecimal = Math.max(0.0001, drDecimal); 

  let totalDiscountedCosts = initialProjectCost; 
  let totalDiscountedBenefits = initialEconomicStimulus; 

  // Calculate total discounted costs and benefits over project lifespan 
  for (let t = 1; t <= projectLifespan; t++) { 
    const discountFactor = (drDecimal <= 0) ? 1 : Math.pow(1 + drDecimal, t); // Use 1 if dr is 0 or less for no discounting 
    totalDiscountedCosts += annualOperatingCost / discountFactor; 
    totalDiscountedBenefits += (annualEconomicBenefit + annualPropertyValueBenefit + annualCommunityBenefitValue) / discountFactor; 
  } 

  const netPresentValue = totalDiscountedBenefits - totalDiscountedCosts; 

  let returnOnInvestment = 0; 
  if (totalDiscountedCosts > 0) { 
    returnOnInvestment = (netPresentValue / totalDiscountedCosts) * 100; 
  } else if (totalDiscountedBenefits > 0) { 
    // If cost is 0 but benefits exist, ROI is effectively infinite 
    returnOnInvestment = Infinity; 
  } else { 
    returnOnInvestment = 0; // No costs, no benefits 
  } 

  // Calculate Payback Period (discounted) 
  let cumulativeNetBenefit = -initialProjectCost + initialEconomicStimulus; // Year 0 net flow 
  let paybackPeriod = '> ' + projectLifespan; // Default if no payback within lifespan 

  if (netPresentValue > 0) { // Only attempt payback calculation if NPV is positive over the entire lifespan 
    let foundPayback = false; 
    for (let t = 1; t <= projectLifespan; t++) { 
      const discountFactor = (drDecimal <= 0) ? 1 : Math.pow(1 + drDecimal, t); 
      const annualNetFlow = (annualEconomicBenefit + annualPropertyValueBenefit + annualCommunityBenefitValue - annualOperatingCost) / discountFactor; 
      cumulativeNetBenefit += annualNetFlow; 

      if (cumulativeNetBenefit >= 0 && !foundPayback) { 
        // Linear interpolation for fractional year 
        // This means the payback occurred in year 't'. We need to figure out how far into 't'. 
        // The amount needed to reach 0 at the start of year 't' was -(cumulativeNetBenefit - annualNetFlow). 
        // The inflow during year 't' was annualNetFlow. 
        paybackPeriod = t - 1 + (Math.abs(cumulativeNetBenefit - annualNetFlow) / annualNetFlow); 
        foundPayback = true; 
        break; 
      } 
    } 
  }

  // Special handling for Infinity ROI for display purposes 
  if (returnOnInvestment === Infinity) { 
    return { 
      totalProjectCost: totalDiscountedCosts, 
      totalProjectBenefits: totalDiscountedBenefits, 
      netPresentValue: netPresentValue, 
      returnOnInvestment: 999999999, // Capped very high for practical display 
      paybackPeriod: initialProjectCost === 0 && totalDiscountedBenefits > 0 ? 0 : '> ' + projectLifespan // Instant payback if no cost and benefits 
    }; 
  }

  return { 
    totalProjectCost: totalDiscountedCosts, 
    totalProjectBenefits: totalDiscountedBenefits, 
    netPresentValue: netPresentValue, 
    returnOnInvestment: returnOnInvestment, 
    paybackPeriod: typeof paybackPeriod === 'number' ? paybackPeriod : paybackPeriod 
  };
