function calculateWellbeing(inputs) {
    const { commuteTime, greenSpaceAccess, noiseLevel, costOfLivingIndex, amenityAccess, safetyPerception, communityEngagement, airQualityPerception } = inputs;

    // Input Validation and Clamping
    const clampedCommute = Math.max(0, Math.min(commuteTime, 180)); // Max 3 hours commute
    const clampedGreenSpace = Math.max(0, Math.min(greenSpaceAccess, 60)); // Max 1 hour walk
    const clampedNoise = Math.max(1, Math.min(noiseLevel, 5));
    const clampedCOL = Math.max(50, Math.min(costOfLivingIndex, 250)); // COL between 50 and 250
    const clampedAmenity = Math.max(0, Math.min(amenityAccess, 30)); // Max 30 min walk
    const clampedSafety = Math.max(1, Math.min(safetyPerception, 5));
    const clampedCommunity = Math.max(1, Math.min(communityEngagement, 5));
    const clampedAirQuality = Math.max(1, Math.min(airQualityPerception, 5));

    // 1. Normalize and Score Each Factor (0-100 where higher is better)

    // Commute Time (Lower is better, ideal 0-30 mins, penalize heavily above 60) - Weight 20%
    let commuteScore;
    if (clampedCommute <= 30) { // Excellent commute
        commuteScore = 100;
    } else if (clampedCommute <= 60) { // Good to acceptable
        commuteScore = 100 - ((clampedCommute - 30) / 30) * 30; // 30 mins -> 100, 60 mins -> 70
    } else {
        commuteScore = 70 - ((clampedCommute - 60) / 120) * 70; // 60 mins -> 70, 180 mins -> 0
    }
    commuteScore = Math.max(0, Math.min(100, commuteScore));

    // Green Space Access (Lower is better, ideal 0-5 mins) - Weight 15%
    let greenSpaceScore;
    if (clampedGreenSpace <= 5) { // Excellent access
        greenSpaceScore = 100;
    } else if (clampedGreenSpace <= 20) { // Good access
        greenSpaceScore = 100 - ((clampedGreenSpace - 5) / 15) * 40; // 5 mins -> 100, 20 mins -> 60
    } else {
        greenSpaceScore = 60 - ((clampedGreenSpace - 20) / 40) * 60; // 20 mins -> 60, 60 mins -> 0
    }
    greenSpaceScore = Math.max(0, Math.min(100, greenSpaceScore));

    // Noise Level (1=Very Quiet -> 5=Very Noisy) - Weight 10%
    // Score: 1 -> 100, 2 -> 80, 3 -> 60, 4 -> 40, 5 -> 20
    const noiseScore = (6 - clampedNoise) * 20;

    // Cost of Living Index (100 = average, lower is better for wellbeing) - Weight 20%
    // Base score is 100. Adjust based on deviation from 100.
    let costOfLivingScore;
    if (clampedCOL <= 100) {
        costOfLivingScore = 100 + (100 - clampedCOL) * 0.8; // e.g., COL 80 -> 100 + 16 = 116
    } else {
        costOfLivingScore = 100 - (clampedCOL - 100) * 1.2; // e.g., COL 120 -> 100 - 24 = 76
    }
    costOfLivingScore = Math.max(0, Math.min(130, costOfLivingScore)); // Cap at 130 for exceptionally low COL, Floor at 0

    // Amenity Access (Lower is better, ideal 0-5 mins) - Weight 10%
    let amenityScore;
    if (clampedAmenity <= 5) {
        amenityScore = 100;
    } else if (clampedAmenity <= 15) {
        amenityScore = 100 - ((clampedAmenity - 5) / 10) * 35; // 5 mins -> 100, 15 mins -> 65
    } else {
        amenityScore = 65 - ((clampedAmenity - 15) / 15) * 65; // 15 mins -> 65, 30 mins -> 0
    }
    amenityScore = Math.max(0, Math.min(100, amenityScore));

    // Safety Perception (1=Very Unsafe -> 5=Very Safe) - Weight 10%
    const safetyScore = clampedSafety * 20;

    // Community Engagement (1=Low -> 5=High) - Weight 5%
    const communityScore = clampedCommunity * 20;

    // Air Quality Perception (1=Very Poor -> 5=Excellent) - Weight 10%
    const airQualityScore = clampedAirQuality * 20;

    // 2. Apply Weights and Aggregate Scores
    const weightedScore = (
        commuteScore * 0.20 +
        greenSpaceScore * 0.15 +
        noiseScore * 0.10 +
        costOfLivingScore * 0.20 +
        amenityScore * 0.10 +
        safetyScore * 0.10 +
        communityScore * 0.05 +
        airQualityScore * 0.10
    );

    // Final Wellbeing Index is already scaled 0-100
    const wellbeingIndex = parseFloat(weightedScore.toFixed(1));

    // 3. Determine Qualitative Feedback
    let qualitativeFeedback;
    if (wellbeingIndex >= 85) {
        qualitativeFeedback = "Outstanding urban wellbeing! Your environment strongly supports a high quality of life.";
    } else if (wellbeingIndex >= 70) {
        qualitativeFeedback = "Excellent urban wellbeing. Your living conditions are highly favorable.";
    } else if (wellbeingIndex >= 50) {
        qualitativeFeedback = "Good urban wellbeing. Your area offers a decent quality of life with room for improvement.";
    } else if (wellbeingIndex >= 30) {
        qualitativeFeedback = "Moderate urban wellbeing. There are significant areas for improvement to enhance your quality of life.";
    } else {
        qualitativeFeedback = "Challenging urban wellbeing. Your environment may be significantly impacting your quality of life. Consider areas for change.";
    }

    return {
        wellbeingIndex: wellbeingIndex,
        qualitativeFeedback: qualitativeFeedback
    };
}
