const exposurePublicTension = Number(inputs.exposurePublicTension || 0);
const safetyWalkAloneNight = Number(inputs.safetyWalkAloneNight || 0);
const socialDisorderExposure = Number(inputs.socialDisorderExposure || 0);
const communityDisruptionProximity = Number(inputs.communityDisruptionProximity || 0);
const commuteStressIndex = Number(inputs.commuteStressIndex || 0);
const accessGreenSpace = Number(inputs.accessGreenSpace || 0);
const newsExposureLocal = Number(inputs.newsExposureLocal || 0);
const communityCohesion = Number(inputs.communityCohesion || 0);

// Input validation and default to 0 if invalid or missing, although 'required' should handle most cases
if ([exposurePublicTension, safetyWalkAloneNight, socialDisorderExposure, communityDisruptionProximity, commuteStressIndex, accessGreenSpace, newsExposureLocal, communityCohesion].some(isNaN)) {
    return { stressIndex: 0 }; // Return 0 or handle as an error if any input is invalid
}

// Step 1: Base stress points from direct tension/disruption exposure
let rawStress = 0;
rawStress += exposurePublicTension * 3; // High weight for direct witnessing
rawStress += socialDisorderExposure * 4; // High weight for frequent disorder
rawStress += communityDisruptionProximity * 5; // Very high weight for proximity to major disruptions
rawStress += commuteStressIndex * 2; // Moderate weight for commute stress
rawStress += newsExposureLocal * 2.5; // Moderate-high weight for media consumption

// Step 2: Adjust for perceived safety (inverted scale: lower safety score means higher stress)
rawStress += (5 - safetyWalkAloneNight) * 4; // Invert: 5-safe -> 0 stress; 1-unsafe -> 4*4=16 stress

// Step 3: Adjust for mitigating factors (inverted scale: higher access/cohesion means lower stress)
rawStress += (5 - accessGreenSpace) * 2; // Invert: 5-daily access -> 0 stress; 1-never -> 4*2=8 stress
rawStress += (5 - communityCohesion) * 3; // Invert: 5-strong cohesion -> 0 stress; 1-weak -> 4*3=12 stress

// Step 4: Apply minimum and maximum caps for raw stress for a plausible range
const MIN_RAW_STRESS = 0; // Theoretical minimum
const MAX_RAW_STRESS = 100; // Cap to prevent excessively high scores before normalization
rawStress = Math.max(MIN_RAW_STRESS, Math.min(rawStress, MAX_RAW_STRESS));

// Step 5: Normalize to a 1-100 scale for the final index
// Max possible raw stress with current weights and max inputs:
// expTension: 7*3 = 21
// socialDisorder: 5*4 = 20
// commDisruption: 5*5 = 25
// commuteStress: 5*2 = 10
// newsExposure: 4*2.5 = 10
// safetyWalk (1): (5-1)*4 = 16
// accessGreen (1): (5-1)*2 = 8
// cohesion (1): (5-1)*3 = 12
// Total max = 21+20+25+10+10+16+8+12 = 122

const NORMALIZATION_FACTOR = 122; // The maximum theoretical raw stress score
let stressIndex = Math.round((rawStress / NORMALIZATION_FACTOR) * 99) + 1; // Scale to 1-100

// Ensure the index is within the 1-100 range
stressIndex = Math.max(1, Math.min(100, stressIndex));

return { stressIndex: stressIndex };
