function calculate({ total_property_area_sqft, impervious_area_sqft, rainfall_intensity_in_hr, design_storm_duration_hr, green_infrastructure_reduction_percent }) {

    // Input validation and default values
    total_property_area_sqft = Math.max(0, total_property_area_sqft || 0);
    impervious_area_sqft = Math.max(0, impervious_area_sqft || 0);
    rainfall_intensity_in_hr = Math.max(0, rainfall_intensity_in_hr || 0);
    design_storm_duration_hr = Math.max(0, design_storm_duration_hr || 0);
    green_infrastructure_reduction_percent = Math.max(0, Math.min(100, green_infrastructure_reduction_percent || 0)); // Cap GI reduction at 100%

    // Ensure impervious area does not exceed total property area
    impervious_area_sqft = Math.min(impervious_area_sqft, total_property_area_sqft);

    const pervious_area_sqft = total_property_area_sqft - impervious_area_sqft;

    // Typical runoff coefficients for urban areas
    const C_impervious = 0.9; // e.g., roofs, pavement
    const C_pervious = 0.15;  // e.g., average urban lawn/landscape

    let weighted_C = 0;
    if (total_property_area_sqft > 0) {
        weighted_C = (impervious_area_sqft * C_impervious + pervious_area_sqft * C_pervious) / total_property_area_sqft;
    }

    // Apply green infrastructure reduction to the overall runoff coefficient
    // GI can reduce the effective runoff, but not completely eliminate it for all rainfall scenarios.
    // The multiplier ensures that even with 100% GI reduction input, C doesn't go below 0.1 of its original value
    // to reflect that some runoff is almost always inevitable.
    const gi_reduction_multiplier = Math.max(0.1, 1 - (green_infrastructure_reduction_percent / 100));
    let effective_runoff_coefficient = weighted_C * gi_reduction_multiplier;

    // Ensure a minimum C value to prevent zero runoff in all cases for realism
    effective_runoff_coefficient = Math.max(0.05, effective_runoff_coefficient);

    // Convert area from sqft to acres for Rational Method (Q=CIA) calculations
    const total_property_area_acres = total_property_area_sqft / 43560; // 1 acre = 43560 sq ft

    // Calculate Runoff Volume (in cubic feet)
    // Volume (acre-inches) = C * I * A * Duration
    // 1 acre-inch = 3630 cubic feet
    const runoff_volume_cuft = effective_runoff_coefficient * rainfall_intensity_in_hr * total_property_area_acres * design_storm_duration_hr * 3630;

    // Calculate Peak Flow Rate (in cfs) using the Rational Method: Q = C * I * A
    // Q (cfs) = C (dimensionless) * I (inches/hour) * A (acres)
    const peak_flow_rate_cfs = effective_runoff_coefficient * rainfall_intensity_in_hr * total_property_area_acres;

    return {
        runoff_volume_cuft: runoff_volume_cuft,
        peak_flow_rate_cfs: peak_flow_rate_cfs
    };
}
