/**
 * Calculates the Urban Stress Index based on various environmental and social factors.
 * The index ranges from 0 (lowest stress) to 100 (highest stress).
 *
 * @param {object} inputs - An object containing all input values.
 * @param {number} inputs.populationDensity - Population density in people/km².
 * @param {number} inputs.noisePollution - Average noise level in dBA.
 * @param {number} inputs.greenSpaceAccess - Percentage of area within 500m with green space.
 * @param {number} inputs.crimeRate - Annual crime incidents per 1000 people.
 * @param {number} inputs.commuteTime - Average commute time in minutes.
 * @param {number} inputs.healthcareAccess - Number of healthcare facilities per 10,000 people.
 * @param {number} inputs.airQualityIndex - Average Air Quality Index (AQI).
 * @param {number} inputs.perceivedSafety - Perceived safety score (1-10, 10 being safest).
 * @returns {object} An object containing the calculated urbanStressIndex.
 */
function calculateUrbanStressIndex(inputs) {
  const MAX_PARTIAL_SCORE = 12.5; // Each of 8 factors contributes max 12.5 points
  let totalStressScore = 0;

  // Input validation and clamping to ensure positive numbers and reasonable ranges
  const populationDensity = Math.max(0, inputs.populationDensity);
  const noisePollution = Math.max(0, inputs.noisePollution);
  const greenSpaceAccess = Math.max(0, Math.min(100, inputs.greenSpaceAccess));
  const crimeRate = Math.max(0, inputs.crimeRate);
  const commuteTime = Math.max(0, inputs.commuteTime);
  const healthcareAccess = Math.max(0, inputs.healthcareAccess);
  const airQualityIndex = Math.max(0, inputs.airQualityIndex);
  const perceivedSafety = Math.max(1, Math.min(10, inputs.perceivedSafety)); // Score 1-10

  // --- 1. Population Density Stress (Higher density = Higher stress) ---
  // Reference: Low stress < 500, High stress > 5000
  const densityStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (populationDensity - 500) / 4500 * MAX_PARTIAL_SCORE));
  totalStressScore += densityStress;

  // --- 2. Noise Pollution Stress (Higher dBA = Higher stress) ---
  // Reference: Low stress < 40 dBA, High stress > 80 dBA
  const noiseStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (noisePollution - 40) / 40 * MAX_PARTIAL_SCORE));
  totalStressScore += noiseStress;

  // --- 3. Green Space Access Stress (Lower % = Higher stress) ---
  // Reference: Low stress > 50%, High stress < 10%
  const greenSpaceStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (50 - greenSpaceAccess) / 40 * MAX_PARTIAL_SCORE));
  totalStressScore += greenSpaceStress;

  // --- 4. Crime Rate Stress (Higher incidents = Higher stress) ---
  // Reference: Low stress < 5, High stress > 50 incidents/1000 people
  const crimeStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (crimeRate - 5) / 45 * MAX_PARTIAL_SCORE));
  totalStressScore += crimeStress;

  // --- 5. Commute Time Stress (Longer time = Higher stress) ---
  // Reference: Low stress < 15 min, High stress > 90 min
  const commuteStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (commuteTime - 15) / 75 * MAX_PARTIAL_SCORE));
  totalStressScore += commuteStress;

  // --- 6. Healthcare Access Stress (Lower facilities = Higher stress) ---
  // Reference: Low stress > 5 facilities, High stress < 0.5 facilities/10,000 people
  const healthcareStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (5 - healthcareAccess) / 4.5 * MAX_PARTIAL_SCORE));
  totalStressScore += healthcareStress;

  // --- 7. Air Quality Index Stress (Higher AQI = Higher stress) ---
  // Reference: Low stress < 50 AQI, High stress > 200 AQI
  const aqStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (airQualityIndex - 50) / 150 * MAX_PARTIAL_SCORE));
  totalStressScore += aqStress;

  // --- 8. Perceived Safety Stress (Lower score = Higher stress) ---
  // Reference: Low stress > 8, High stress < 3 (on 1-10 scale)
  const safetyStress = Math.min(MAX_PARTIAL_SCORE, Math.max(0, (8 - perceivedSafety) / 5 * MAX_PARTIAL_SCORE));
  totalStressScore += safetyStress;

  // Final index clamping between 0 and 100
  const urbanStressIndex = Math.max(0, Math.min(100, totalStressScore));

  return {
    urbanStressIndex: urbanStressIndex
  };
}
