function calculateCosts(inputs) {
  const areaAffectedSqM = parseFloat(inputs.areaAffectedSqM);
  const primaryInfrastructureType = inputs.primaryInfrastructureType;
  const damageLevel = inputs.damageLevel;
  const localMaterialCostIndex = parseFloat(inputs.localMaterialCostIndex);
  const localLaborCostIndex = parseFloat(inputs.localLaborCostIndex);
  const logisticalChallengeLevel = inputs.logisticalChallengeLevel;
  const contingencyBufferPercent = parseFloat(inputs.contingencyBufferPercent);
  const securityOverheadPercent = parseFloat(inputs.securityOverheadPercent);

  // Input validation and defaults
  if (isNaN(areaAffectedSqM) || areaAffectedSqM <= 0) return { error: "Area Affected must be a positive number." };
  if (isNaN(localMaterialCostIndex) || localMaterialCostIndex <= 0) return { error: "Material Cost Index must be a positive number." };
  if (isNaN(localLaborCostIndex) || localLaborCostIndex <= 0) return { error: "Labor Cost Index must be a positive number." };
  if (isNaN(contingencyBufferPercent) || contingencyBufferPercent < 0) return { error: "Contingency Buffer must be a non-negative number." };
  if (isNaN(securityOverheadPercent) || securityOverheadPercent < 0) return { error: "Security Overhead must be a non-negative number." };

  // Base costs per square meter (Material & Labor combined, average values)
  // These are illustrative and would be derived from detailed construction cost databases
  const baseCostsPerSqM = {
    "Public Buildings": {
      "Minor Cosmetic": { material: 250, labor: 180, logisticsFactor: 0.05 },
      "Moderate Structural": { material: 600, labor: 450, logisticsFactor: 0.10 },
      "Severe Destruction": { material: 1200, labor: 900, logisticsFactor: 0.15 }
    },
    "Roads & Pavements": {
      "Minor Cosmetic": { material: 80, labor: 60, logisticsFactor: 0.03 },
      "Moderate Structural": { material: 200, labor: 150, logisticsFactor: 0.07 },
      "Severe Destruction": { material: 400, labor: 300, logisticsFactor: 0.12 }
    },
    "Commercial Facades": {
      "Minor Cosmetic": { material: 300, labor: 220, logisticsFactor: 0.06 },
      "Moderate Structural": { material: 700, labor: 550, logisticsFactor: 0.11 },
      "Severe Destruction": { material: 1400, labor: 1100, logisticsFactor: 0.18 }
    },
    "Utility Networks": {
      "Minor Cosmetic": { material: 150, labor: 120, logisticsFactor: 0.08 },
      "Moderate Structural": { material: 400, labor: 300, logisticsFactor: 0.13 },
      "Severe Destruction": { material: 800, labor: 600, logisticsFactor: 0.20 }
    },
    "Public Green Spaces": {
      "Minor Cosmetic": { material: 30, labor: 25, logisticsFactor: 0.02 },
      "Moderate Structural": { material: 90, labor: 70, logisticsFactor: 0.05 },
      "Severe Destruction": { material: 180, labor: 140, logisticsFactor: 0.08 }
    }
  };

  const infrastructureCategory = baseCostsPerSqM[primaryInfrastructureType];
  if (!infrastructureCategory) return { error: "Invalid Infrastructure Type selected." };

  const damageCategory = infrastructureCategory[damageLevel];
  if (!damageCategory) return { error: "Invalid Damage Level selected for the chosen Infrastructure Type." };

  let baseMaterialCostPerSqM = damageCategory.material;
  let baseLaborCostPerSqM = damageCategory.labor;
  let baseLogisticsFactor = damageCategory.logisticsFactor;

  // Adjust costs based on local indices
  const materialCost = areaAffectedSqM * baseMaterialCostPerSqM * localMaterialCostIndex;
  const laborCost = areaAffectedSqM * baseLaborCostPerSqM * localLaborCostIndex;

  // Logistical challenge multiplier
  let logisticsMultiplier = 1;
  switch (logisticalChallengeLevel) {
    case "Low (Easy Access)":
      logisticsMultiplier = 1.0;
      break;
    case "Medium (Some Access Issues)":
      logisticsMultiplier = 1.2;
      break;
    case "High (Restricted Access/Security Concerns)":
      logisticsMultiplier = 1.5;
      break;
    default:
      return { error: "Invalid Logistical Challenge Level." };
  }

  const estimatedLogisticsCost = (materialCost + laborCost) * baseLogisticsFactor * logisticsMultiplier;

  const subtotalCost = materialCost + laborCost + estimatedLogisticsCost;

  // Contingency and Security Overhead
  const contingencyCost = subtotalCost * (contingencyBufferPercent / 100);
  const securityOverheadCost = subtotalCost * (securityOverheadPercent / 100);

  const totalReconstructionCost = subtotalCost + contingencyCost + securityOverheadCost;

  return {
    estimatedMaterialCost: materialCost,
    estimatedLaborCost: laborCost,
    estimatedLogisticsCost: estimatedLogisticsCost,
    contingencyCost: contingencyCost,
    securityOverheadCost: securityOverheadCost,
    totalReconstructionCost: totalReconstructionCost
  };
}
