function calculateVariableAnnuityPayout(inputs) {
  let { initialAccountValue, expectedAnnualReturn, annualFeesRate, annualWithdrawalRate, payoutDurationYears, inflationRate } = inputs;

  // Convert percentages to decimals
  const netGrowthRateDecimal = (expectedAnnualReturn - annualFeesRate) / 100;
  const withdrawalRateDecimal = annualWithdrawalRate / 100;
  const inflationRateDecimal = (inflationRate || 0) / 100;

  let currentAccountValue = initialAccountValue;
  let totalNominalPayout = 0;
  let totalRealPayoutSum = 0;
  let annualPayouts = [];

  for (let i = 1; i <= payoutDurationYears; i++) {
    // Calculate potential withdrawal based on current account value
    let potentialWithdrawal = currentAccountValue * withdrawalRateDecimal;

    // Actual withdrawal is capped at the remaining account value
    let actualWithdrawal = Math.min(potentialWithdrawal, Math.max(0, currentAccountValue));

    // Deduct withdrawal from the account value
    currentAccountValue -= actualWithdrawal;

    // Apply growth (or decline) to the remaining balance
    currentAccountValue *= (1 + netGrowthRateDecimal);

    // Ensure account value doesn't go below zero after growth
    currentAccountValue = Math.max(0, currentAccountValue);

    totalNominalPayout += actualWithdrawal;
    annualPayouts.push(actualWithdrawal);

    // Calculate real payout for this year
    const realWithdrawalThisYear = actualWithdrawal / Math.pow(1 + inflationRateDecimal, i);
    totalRealPayoutSum += realWithdrawalThisYear;
  }

  const averageAnnualNominalPayout = totalNominalPayout / payoutDurationYears;
  const averageAnnualRealPayout = totalRealPayoutSum / payoutDurationYears;

  return {
    averageAnnualNominalPayout,
    totalNominalPayout,
    averageAnnualRealPayout,
    endingAccountValue: currentAccountValue
  };
}
