const minVentilation_occupants = inputs.minVentilationPerPerson * inputs.numberOfOccupants;
const minVentilation_area = inputs.minVentilationPerArea * inputs.spaceArea;
const minVentilationRequirement = Math.max(minVentilation_occupants, minVentilation_area);

const minCirculationRequirement = inputs.designCoolingAirflow * (inputs.minAirflowPercentage / 100);

const vavBoxMinFlowSetpoint = Math.max(minVentilationRequirement, minCirculationRequirement);

let minFlowToCoolingRatio = (vavBoxMinFlowSetpoint / inputs.designCoolingAirflow) * 100;
if (minFlowToCoolingRatio > 100) {
    minFlowToCoolingRatio = 100;
}

return {
    minVentilationRequirement: minVentilationRequirement,
    minCirculationRequirement: minCirculationRequirement,
    vavBoxMinFlowSetpoint: vavBoxMinFlowSetpoint,
    minFlowToCoolingRatio: minFlowToCoolingRatio
};
