function calculate({
    currentProductionBPD,
    maxPotentialProductionBPD,
    sanctionReliefLevel,
    annualInvestmentMillionUSD,
    crudePriceUSD,
    investmentHorizonYears,
    recoveryEfficiencyFactor
}) {
    // --- Constants and Assumptions ---
    const PRODUCTION_BOOST_PER_BILLION_USD = 100000; // bpd per $1 Billion investment, baseline
    const BASE_GDP_MULTIPLIER = 0.15; // Rough estimate of oil revenue's direct contribution to GDP
    const MAX_PRODUCTION_GROWTH_PER_YEAR_PCT = 0.20; // Max 20% growth per year from current, regardless of investment, due to infrastructure, expertise limits.
    const BPD_TO_BARRELS_PER_YEAR = 365;

    let productionGrowthPotentialBPD = 0;
    let effectiveInvestmentBillionUSD = annualInvestmentMillionUSD / 1000; // Convert Million USD to Billion USD

    // Calculate production boost from investment, adjusted for sanction relief and efficiency
    // Sanction relief acts as a multiplier on investment effectiveness
    productionGrowthPotentialBPD = effectiveInvestmentBillionUSD * PRODUCTION_BOOST_PER_BILLION_USD * (sanctionReliefLevel / 100) * (recoveryEfficiencyFactor / 100);

    // Cap annual production growth to a realistic maximum percentage of current production
    const maxAllowedGrowthBPD = currentProductionBPD * MAX_PRODUCTION_GROWTH_PER_YEAR_PCT;
    productionGrowthPotentialBPD = Math.min(productionGrowthPotentialBPD, maxAllowedGrowthBPD);

    let projectedProductionBPD = currentProductionBPD;
    let cumulativeProductionIncrease = 0;
    let totalProjectedRevenueMillionUSD = 0;
    let yearsToMaxPotential = 'N/A';
    let reachedMaxPotential = false;

    // Handle edge case where no growth is possible or expected
    if (sanctionReliefLevel === 0 || annualInvestmentMillionUSD === 0 || recoveryEfficiencyFactor === 0) {
        return {
            projectedProductionBPD: Math.round(currentProductionBPD),
            cumulativeProductionIncrease: 0,
            totalProjectedRevenueMillionUSD: parseFloat((currentProductionBPD * BPD_TO_BARRELS_PER_YEAR * crudePriceUSD / 1_000_000 * investmentHorizonYears).toFixed(2)), // Baseline revenue without growth
            avgAnnualGDPContributionMillionUSD: parseFloat((currentProductionBPD * BPD_TO_BARRELS_PER_YEAR * crudePriceUSD / 1_000_000 * BASE_GDP_MULTIPLIER).toFixed(2)),
            yearsToMaxPotential: 'N/A (No growth)'
        };
    }

    for (let year = 1; year <= investmentHorizonYears; year++) {
        let annualProductionIncrease = productionGrowthPotentialBPD;
        let newProductionLevel = projectedProductionBPD + annualProductionIncrease;

        // Ensure production does not exceed max potential
        if (newProductionLevel > maxPotentialProductionBPD) {
            newProductionLevel = maxPotentialProductionBPD;
            if (!reachedMaxPotential) {
                yearsToMaxPotential = year;
                reachedMaxPotential = true;
            }
        }

        let annualProductionDelta = newProductionLevel - projectedProductionBPD;
        cumulativeProductionIncrease += annualProductionDelta * BPD_TO_BARRELS_PER_YEAR;

        // Calculate annual revenue from the *new* production level for this year
        let annualRevenue = newProductionLevel * BPD_TO_BARRELS_PER_YEAR * crudePriceUSD;
        totalProjectedRevenueMillionUSD += annualRevenue / 1_000_000; // Convert to Million USD

        projectedProductionBPD = newProductionLevel;
    }

    if (!reachedMaxPotential && projectedProductionBPD < maxPotentialProductionBPD) {
        yearsToMaxPotential = 'Not reached within horizon';
    }

    // Average Annual GDP Contribution
    const avgAnnualGDPContributionMillionUSD = totalProjectedRevenueMillionUSD / investmentHorizonYears * BASE_GDP_MULTIPLIER;

    return {
        projectedProductionBPD: Math.round(projectedProductionBPD),
        cumulativeProductionIncrease: Math.round(cumulativeProductionIncrease),
        totalProjectedRevenueMillionUSD: parseFloat(totalProjectedRevenueMillionUSD.toFixed(2)),
        avgAnnualGDPContributionMillionUSD: parseFloat(avgAnnualGDPContributionMillionUSD.toFixed(2)),
        yearsToMaxPotential: yearsToMaxPotential
    };
}
